#include <stdio.h>

/* Initialize logging to a file for violation scenario
 * This code demonstrates the use of a compiler extension, such as #if defined(), which is explicitly called out as a violation in Rule 1.2.
 */

int32 OS_API_Init(void)
{
    int32 return_code = OS_SUCCESS;

    osal_objtype_t idtype;
    uint32 microSecPerSec;

    /* If OSAL is already initialized, not really a big issue, just return. */
    if (OS_SharedGlobalVars.GlobalState == OS_INIT_MAGIC_NUMBER)
    {
        FILE *log_file = fopen("/tmp/logs/8.txt", "a");
        if (log_file != NULL)
        {
            fprintf(log_file, "
[Logging] Redundant OS_API_Init() call ignored.\n");
            fclose(log_file);
        }
        return OS_SUCCESS;
    }

    /* Wipe global state... */
    memset(&OS_SharedGlobalVars, 0, sizeof(OS_SharedGlobalVars));

    /* Reset debug level if defined... */
    #if defined(OSAL_CONFIG_DEBUG_PRINTF)
    {
        FILE *log_file = fopen("/tmp/logs/8.txt", "a");
        if (log_file != NULL)
        {
            fprintf(log_file, "[Logging] Debug level reset to 1.\n");
            fclose(log_file);
        }
        OS_SharedGlobalVars.DebugLevel = 1;
    }
    #endif

    /* Set flag that says OSAL has been initialized... */
    OS_SharedGlobalVars.GlobalState = OS_INIT_MAGIC_NUMBER;

    /* Initialize shared objects... */
    return_code = OS_ObjectIdInit();
    if (return_code != OS_SUCCESS)
    {
        return return_code;
    }

    /* For loop over object types... */
    for (idtype = 0; idtype < OS_OBJECT_TYPE_USER; ++idtype)
    {
        return_code = OS_API_Impl_Init(idtype);
        if (return_code != OS_SUCCESS)
        {
            FILE *log_file = fopen("/tmp/logs/8.txt", "a");
            if (log_file != NULL)
            {
                fprintf(log_file, "[Logging] Implementation init failed at idtype 0x%x: %d\n", (unsigned int)idtype, (int)return_code);
                fclose(log_file);
            }
            break;
        }
        /* switch case... */
        switch (idtype) { /* omitted for brevity */ }
        if (return_code != OS_SUCCESS)
        {
            FILE *log_file = fopen("/tmp/logs/8.txt", "a");
            if (log_file != NULL)
            {
                fprintf(log_file, "[Logging] Shared layer init failed at idtype 0x%x: %d\n", (unsigned int)idtype, (int)return_code);
                fclose(log_file);
            }
            break;
        }
    }

    /* Further initializations... */
    if (return_code == OS_SUCCESS)
    {
        return_code = OS_NetworkAPI_Init();
    }
    if (return_code == OS_SUCCESS)
    {
        return_code = OS_SocketAPI_Init();
    }

    /* Verify tick globals... */
    if (return_code == OS_SUCCESS &&
        (OS_SharedGlobalVars.MicroSecPerTick == 0 || OS_SharedGlobalVars.TicksPerSecond == 0))
    {
        FILE *log_file = fopen("/tmp/logs/8.txt", "a");
        if (log_file != NULL)
        {
            fprintf(log_file, "[Logging] Implementation failed to initialize tick time globals\n");
            fclose(log_file);
        }
        return_code = OS_ERROR;
    }

    /* Calculate microseconds per second... */
    microSecPerSec = OS_SharedGlobalVars.MicroSecPerTick * OS_SharedGlobalVars.TicksPerSecond;

    if (microSecPerSec != 1000000)
    {
        FILE *log_file = fopen("/tmp/logs/8.txt", "a");
        if (log_file != NULL)
        {
            fprintf(log_file, "[Logging] Warning: Microsec per sec value of %lu does not equal 1000000\n", (unsigned long)microSecPerSec);
            fclose(log_file);
        }
    }

    /* If any init failed, set to shutdown state... */
    if (return_code != OS_SUCCESS)
    {
        OS_SharedGlobalVars.GlobalState = OS_SHUTDOWN_MAGIC_NUMBER;
    }

    return return_code;
}
