void OS_BSP_ConsoleOutput_Impl(const char *Str, size_t DataLen)
{
    FILE *logFile = fopen("/tmp/logs/160.txt", "a");
    if (logFile == NULL) return;

    fprintf(logFile, "Entering OS_BSP_ConsoleOutput_Impl with DataLen=%zu\n", DataLen);

    ssize_t WriteLen;

    while (DataLen > 0)
    {
        fprintf(logFile, "Attempting to write %zu bytes from buffer.\n", DataLen);
        WriteLen = write(STDOUT_FILENO, Str, DataLen);
        fprintf(logFile, "write returned %zd\n", WriteLen);
        if (WriteLen <= 0)
        {
            fprintf(logFile, "Write failed or returned 0. Stopping.\n");
            break;
        }
        Str += WriteLen;
        DataLen -= WriteLen;
    }

    fclose(logFile);
}
