void log_dead_code_violation(const char *path, bool condition) {
    FILE *file = fopen("/tmp/logs/266.txt", "a");
    if (file != NULL) {
        fprintf(file, "Dead code detected at path: %s, condition: %d\n", path, condition);
        fclose(file);
    }
}

// Modified version of OS_FileSys_FindVirtMountPoint with logging
bool OS_FileSys_FindVirtMountPoint(void *ref, const OS_object_token_t *token, const OS_common_record_t *obj)
{
    OS_filesys_internal_record_t *filesys;
    const char *target = (const char *)ref;
    size_t mplen;

    filesys = OS_OBJECT_TABLE_GET(OS_filesys_table, *token);

    if ((filesys->flags & OS_FILESYS_FLAG_IS_MOUNTED_VIRTUAL) == 0)
    {
        return false;
    }

    mplen = OS_strnlen(filesys->virtual_mountpt, sizeof(filesys->virtual_mountpt));

    log_dead_code_violation(target, true); // log before return
    if (mplen == 0 || mplen >= sizeof(filesys->virtual_mountpt) ||
        strncmp(target, filesys->virtual_mountpt, mplen) != 0)
    {
        log_dead_code_violation(target, false); // log before return
        return false;
    }

    // Confirm that the substring ends at either a directory separator or the end of string
    // No dead code after this point
    return (target[mplen] == '/' || target[mplen] == 0);
}
