static void *OS_PthreadTaskEntry(void *arg)
{
    OS_VoidPtrValueWrapper_t local_arg;

    /* Logging the entry and argument pointer */
    FILE *log_file = fopen("/tmp/logs/72.txt", "a");
    if (log_file != NULL) {
        fprintf(log_file, "Entering OS_PthreadTaskEntry with arg at %p\n", arg);
    }

    /* cppcheck-suppress unreadVariable // intentional use of other union member */
    local_arg.opaque_arg = arg;

    if (log_file != NULL) {
        fprintf(log_file, "local_arg.opaque_arg set to %p\n", local_arg.opaque_arg);
    }

    OS_TaskEntryPoint(local_arg.id); /* Never returns */

    if (log_file != NULL) {
        fprintf(log_file, "Exiting OS_PthreadTaskEntry\n");
        fclose(log_file);
    }

    return NULL;
}
