#include <stdio.h>
#include <string.h>

// Probe functions for logging
void log_control(const char *message) {
    FILE *logfile = fopen("/tmp/logs/34.txt", "a");
    if (logfile) {
        fprintf(logfile, "%s\n", message);
        fclose(logfile);
    }
}

int32 OS_SocketRecvFrom_Impl(const OS_object_token_t *token, void *buffer, size_t buflen, OS_SockAddr_t *RemoteAddr,
                             int32 timeout)
{
    int32                           return_code;
    int                             os_result;
    int                             waitflags;
    uint32                          operation;
    struct sockaddr *               sa;
    socklen_t                       addrlen;
    OS_impl_file_internal_record_t *impl;

    impl = OS_OBJECT_TABLE_GET(OS_impl_filehandle_table, *token);

    if (RemoteAddr == NULL)
    {
        sa      = NULL;
        addrlen = 0;
        log_control("RemoteAddr is NULL, set sa to NULL and addrlen to 0.");
    }
    else
    {
        addrlen = OS_SOCKADDR_MAX_LEN;
        sa      = (struct sockaddr *)&RemoteAddr->AddrData;
        log_control("RemoteAddr is not NULL, set sa to point to AddrData.");
    }

    operation = OS_STREAM_STATE_READABLE;
    if (impl->selectable)
    {
        waitflags   = MSG_DONTWAIT;
        log_control("Impl selectable is true, calling OS_SelectSingle_Impl with MSG_DONTWAIT.");
        return_code = OS_SelectSingle_Impl(token, &operation, timeout);
    }
    else
    {
        if (timeout == 0)
        {
            waitflags = MSG_DONTWAIT;
        }
        else
        {
            waitflags = 0;
        }
        return_code = OS_SUCCESS;
        log_control("Impl not selectable, setting waitflags based on timeout, assume success.");
    }

    if (return_code == OS_SUCCESS)
    {
        if ((operation & OS_STREAM_STATE_READABLE) == 0)
        {
            return_code = OS_ERROR_TIMEOUT;
            log_control("Operation not readable, timeout error.");
        }
        else
        {
            os_result = recvfrom(impl->fd, buffer, buflen, waitflags, sa, &addrlen);
            if (os_result < 0)
            {
                if (errno == EAGAIN || errno == EWOULDBLOCK)
                {
                    return_code = OS_QUEUE_EMPTY;
                    log_control("recvfrom returned EAGAIN or EWOULDBLOCK, queue empty.");
                }
                else
                {
                    OS_DEBUG("recvfrom: %s\n", strerror(errno));
                    return_code = OS_ERROR;
                }
            }
            else
            {
                return_code = os_result;
                if (RemoteAddr != NULL)
                {
                    RemoteAddr->ActualLength = addrlen;
                    log_control("recvfrom successful, set RemoteAddr->ActualLength.");
                }
            }
        }
    }

    return return_code;
}
