void OS_printf(const char *String, ...)
{
    va_list va;
    char    msg_buffer[OS_BUFFER_SIZE];
    int     actualsz;

    BUGCHECK_VOID(String != NULL)

    if (OS_SharedGlobalVars.GlobalState != OS_INIT_MAGIC_NUMBER)
    {
        static FILE *log_file = NULL;
        if (log_file == NULL) {
            log_file = fopen("/tmp/logs/273.txt", "a+");
            if (log_file == NULL) {
                return;
            }
        }
        fprintf(log_file, "[Path: OS_printf] Called when not initialized. String: %s\n", String);
        fflush(log_file);
    }
    else if (OS_SharedGlobalVars.PrintfEnabled)
    {
        va_start(va, String);
        actualsz = vsnprintf(msg_buffer, sizeof(msg_buffer), String, va);
        va_end(va);
        if (actualsz < 0)
        {
            actualsz = 0;
        }
        else if (actualsz >= OS_BUFFER_SIZE)
        {
            actualsz = OS_BUFFER_SIZE - 1;
        }
        msg_buffer[actualsz] = 0;
        static FILE *log_file_inner = NULL;
        if (log_file_inner == NULL) {
            log_file_inner = fopen("/tmp/logs/273.txt", "a+");
            if (log_file_inner == NULL) {
                return;
            }
        }
        fprintf(log_file_inner, "[Path: OS_printf] Message: %s\n", msg_buffer);
        fflush(log_file_inner);
        OS_ConsoleWrite(OS_SharedGlobalVars.PrintfConsoleId, msg_buffer);
    }
}
