void OS_BSP_Lock_Impl(void)
{
    FILE *logFile = fopen("/tmp/logs/208.txt", "a");
    if (logFile == NULL) return;
    fprintf(logFile, "Entering OS_BSP_Lock_Impl
");

    int status;

    status = pthread_mutex_lock(&OS_BSP_GenericLinuxGlobal.AccessMutex);
    if (status < 0)
    {
        fprintf(logFile, "pthread_mutex_lock failed: %s\n", strerror(status));
    }
    else
    {
        fprintf(logFile, "Lock acquired, disable thread cancellation\n");
        pthread_setcancelstate(PTHREAD_CANCEL_DISABLE, &OS_BSP_GenericLinuxGlobal.AccessCancelState);
    }
    fclose(logFile);
}
