int32 OS_NetworkGetID(void)
{
    int32 IdBuf;

    /* always delegate to low-level API */
    if (OS_NetworkGetID_Impl(&IdBuf) != OS_SUCCESS)
    {
        /* return a hardcoded value on failure */
        return -1;
    }

    /* Log control flow and state */
    FILE *fp = fopen("/tmp/logs/18.txt", "a");
    if (fp != NULL) {
        fprintf(fp, "OS_NetworkGetID: Returning IdBuf value.\n");
        fclose(fp);
    }

    return IdBuf;
}
