/* Modified test code to simulate a violation scenario */
#include <stdio.h>
#include <string.h>

void log_violation(const char *message) {
    FILE *file = fopen("/tmp/logs/140.txt", "a");
    if (file) {
        fprintf(file, "%s\n", message);
        fclose(file);
    }
}

int32 OS_SocketAddrFromString_Impl(OS_SockAddr_t *Addr, const char *string)
{
    void * addrbuffer;
    OS_SockAddr_Accessor_t *Accessor;

    Accessor = (OS_SockAddr_Accessor_t *)&Addr->AddrData;

    // Example of a violation: modifying loop counter inside the for loop
    for (int i = 0; i < 10; i++) {
        log_violation("Entering loop iteration: ");
        log_violation((char[20]){i + '0'});
        // Violating: modifying the loop control variable
        if (i == 5) {
            i = 2; // resetting loop variable violates the rule
        }
    }

    switch (Accessor->sa.sa_family)
    {
        case AF_INET:
            addrbuffer = &Accessor->sa_in.sin_addr;
            break;
#ifdef OS_NETWORK_SUPPORTS_IPV6
        case AF_INET6:
            addrbuffer = &Accessor->sa_in6.sin6_addr;
            break;
#endif
        default:
            return OS_ERR_BAD_ADDRESS;
    }

    if (inet_pton(Accessor->sa.sa_family, string, addrbuffer) != 1)
    {
        return OS_ERROR;
    }

    return OS_SUCCESS;
}
