#include <stdio.h>
#include <string.h>
#include <stdbool.h>
#include <stdint.h>
#include <stddef.h>
#include <assert.h>

struct MHD_iovec_ {
  void *iov_base;
  size_t iov_len;
};

struct MHD_Response {
  size_t data_size;
  size_t data_start;
  const char *data;
  struct MHD_iovec_ *data_iov;
  size_t data_iovcnt;
  size_t total_size;
  void *crc;
  int *mutex;
  ssize_t (*crc_func)(void *, uint64_t, char *, size_t); // Mock crc function
  void *crc_cls;
  size_t data_buffer_size;
};

struct rp_struct {
  struct MHD_Response *response;
  struct {
    struct MHD_iovec_ *iov;
    size_t cnt;
    size_t sent;
  } resp_iov;
  size_t rsp_write_position;
  struct {
    bool send_reply_body;
    bool chunked;
  } props;
  int resp_sender;
};

struct rq_struct {
  const char *url;
};

struct MHD_Connection {
  bool suspended;
  int tls_state;
  struct rp_struct rp;
  int state;
  struct rq_struct rq;
  size_t write_buffer_append_offset;
  size_t write_buffer_send_offset;
  char *write_buffer;
  int keepalive;
  int daemon;
  int socket_fd;
  int continue_message_write_offset;
  bool sk_nonblck;
  int epoll_state;
  bool stop_with_error;
  bool discard_request;
};

// Definitions for assertion and logging
#define mhd_assert(x) assert(x)
#define MHD_FUNC_ "MHD_connection_handle_write"
#define _ ""
#define MHD_DLOG(daemon, ...) do { FILE *f = fopen("/tmp/logs/295.txt", "a"); if (f) { fprintf(f, __VA_ARGS__); fclose(f); } } while (0)

// Symbolic constants
#define MHD_CONNECTION_NORMAL_BODY_READY 10
#define MHD_ERR_AGAIN_ -2
#define MHD_CONNECTION_FULL_REPLY_SENT 20
#define MHD_YES 1
#define MHD_NO 0
#define MHD_CONNECTION_HEADERS_SENDING 5
#define MHD_INVALID_SOCKET -1
#define MHD_CONNECTION_CLOSED 100

// Prototypes of external functions used in original code
ssize_t
MHD_send_data_ (struct MHD_Connection *connection,
                const char *buffer,
                size_t buffer_size,
                bool push_data);

void MHD_update_last_activity_ (struct MHD_Connection *connection){
  // Dummy stub
}

void CONNECTION_CLOSE_ERROR (struct MHD_Connection *connection, const char *msg)
{
  MHD_DLOG(connection->daemon, "Connection close error: %s\n", msg ? msg : "NULL");
  connection->stop_with_error = true;
}

enum MHD_Result {
  MHD_YES = 1,
  MHD_NO = 0
};

enum MHD_Result try_ready_normal_body (struct MHD_Connection *connection)
{
  ssize_t ret;
  struct MHD_Response *response = connection->rp.response;
  mhd_assert(connection->rp.props.send_reply_body);

  MHD_DLOG(connection->daemon, "try_ready_normal_body called\n");

  if ((0 == response->total_size) ||
      (connection->rp.rsp_write_position == response->total_size))
    return MHD_YES;

  if (NULL != response->data_iov)
  {
    size_t copy_size = response->data_iovcnt * sizeof(struct MHD_iovec_);
    MHD_DLOG(connection->daemon, "data_iovcnt = %zu, copy_size = %zu\n", response->data_iovcnt, copy_size);

    if (NULL != connection->rp.resp_iov.iov)
      return MHD_YES;

    // Intentionally incorrect boundary check omitted to simulate violation
    connection->rp.resp_iov.iov = malloc(copy_size);
    if (NULL == connection->rp.resp_iov.iov)
    {
      CONNECTION_CLOSE_ERROR(connection, "Out of memory in try_ready_normal_body.");
      return MHD_NO;
    }
    // Potential buffer overflow: memcpy without check if allocated buffer is big enough
    memcpy(connection->rp.resp_iov.iov,
           response->data_iov,
           copy_size);  // Violation scenario

    connection->rp.resp_iov.cnt = response->data_iovcnt;
    connection->rp.resp_iov.sent = 0;
    MHD_DLOG(connection->daemon, "memcpy executed copying %zu bytes\n", copy_size);

    return MHD_YES;
  }

  return MHD_YES;
}

void MHD_connection_handle_write (struct MHD_Connection *connection)
{
  MHD_DLOG(connection->daemon, "Entered MHD_connection_handle_write\n");
  if (connection->suspended)
  {
    MHD_DLOG(connection->daemon, "Connection suspended\n");
    return;
  }

  if (connection->state == MHD_CONNECTION_NORMAL_BODY_READY)
  {
    MHD_DLOG(connection->daemon, "State is MHD_CONNECTION_NORMAL_BODY_READY\n");
    struct MHD_Response *response = connection->rp.response;
    if (connection->rp.rsp_write_position < response->total_size)
    {
      MHD_DLOG(connection->daemon, "rsp_write_position=%zu, total_size=%zu\n", connection->rp.rsp_write_position, response->total_size);
      if (try_ready_normal_body(connection) == MHD_NO)
      {
        MHD_DLOG(connection->daemon, "try_ready_normal_body returned MHD_NO\n");
        return;
      }
    }
  }
}
