#include <stdio.h>
#include <stdlib.h>
#include <string.h>

_MHD_EXTERN enum MHD_Result
MHD_del_response_header (struct MHD_Response *response,
                         const char *header,
                         const char *content)
{
  FILE *log_file = fopen("/tmp/logs/422.txt", "a");

  struct MHD_HTTP_Res_Header *pos;
  size_t header_len;
  size_t content_len;

  if ( (NULL == header) ||
       (NULL == content) ) {
    if (log_file) {
      fprintf(log_file, "Early return: header or content is NULL\n");
      fclose(log_file);
    }
    return MHD_NO;
  }

  header_len = strlen (header);
  if (log_file) {
    fprintf(log_file, "header_len: %zu\n", header_len);
  }

  if ((0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_HDR)) &&
      (MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONNECTION) == header_len) &&
      MHD_str_equal_caseless_bin_n_ (header, MHD_HTTP_HEADER_CONNECTION,
                                     header_len)) {
    if (log_file) {
      fprintf(log_file, "Calling del_response_header_connection\n");
      fclose(log_file);
    }
    return del_response_header_connection (response, content);
  }

  content_len = strlen (content);
  if (log_file) {
    fprintf(log_file, "content_len: %zu\n", content_len);
  }

  pos = response->first_header;
  while (NULL != pos)
  {
    if ((header_len == pos->header_size) &&
        (content_len == pos->value_size) &&
        (0 == memcmp (header,
                      pos->header,
                      header_len)) &&
        (0 == memcmp (content,
                      pos->value,
                      content_len)))
    {
      if (log_file) {
        fprintf(log_file, "Match found for header and content, removing header\n");
      }
      _MHD_remove_header (response, pos);
      free (pos->header);
      free (pos->value);
      free (pos);

      if ( (MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_TRANSFER_ENCODING) ==
            header_len) &&
           MHD_str_equal_caseless_bin_n_ (header,
                                          MHD_HTTP_HEADER_TRANSFER_ENCODING,
                                          header_len) ) {

        response->flags_auto &=
          ~((enum MHD_ResponseAutoFlags) MHD_RAF_HAS_TRANS_ENC_CHUNKED);
        if (log_file) {
          fprintf(log_file, "Cleared MHD_RAF_HAS_TRANS_ENC_CHUNKED flag\n");
        }
      }
      else if ( (MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_DATE) ==
                 header_len) &&
                MHD_str_equal_caseless_bin_n_ (header,
                                               MHD_HTTP_HEADER_DATE,
                                               header_len) ) {

        response->flags_auto &=
          ~((enum MHD_ResponseAutoFlags) MHD_RAF_HAS_DATE_HDR);
        if (log_file) {
          fprintf(log_file, "Cleared MHD_RAF_HAS_DATE_HDR flag\n");
        }
      }
      else if ( (MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONTENT_LENGTH) ==
                 header_len) &&
                MHD_str_equal_caseless_bin_n_ (header,
                                               MHD_HTTP_HEADER_CONTENT_LENGTH,
                                               header_len) )
      {
        if (NULL == MHD_get_response_element_n_ (response,
                                                 MHD_HEADER_KIND,
                                                 MHD_HTTP_HEADER_CONTENT_LENGTH,
                                                 header_len))
          response->flags_auto &=
            ~((enum MHD_ResponseAutoFlags) MHD_RAF_HAS_CONTENT_LENGTH);
        if (log_file) {
          fprintf(log_file, "Checked and possibly cleared MHD_RAF_HAS_CONTENT_LENGTH flag\n");
        }
      }

      if (log_file) {
        fclose(log_file);
      }
      return MHD_YES;
    }
    else {
      if (log_file) {
        fprintf(log_file, "No match at current header node, moving to next\n");
      }
    }
    pos = pos->next;
  }

  if (log_file) {
    fprintf(log_file, "No matching header found, returning MHD_NO\n");
    fclose(log_file);
  }
  return MHD_NO;
}
