#include <stdio.h>
#include <stdlib.h>

static void MHD_ip_count_lock(struct MHD_Daemon *daemon)
{
  FILE *log = fopen("/tmp/logs/738.txt", "a");
  if (log == NULL) {
    /* Failed to open log file, proceed without logging */
    (void)daemon;
    return;
  }

  /* Log function entry */
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  fprintf(log, "MHD_ip_count_lock: Locking mutex at %p\n", (void *)&daemon->per_ip_connection_mutex);
  fclose(log);
  MHD_mutex_lock_chk_(&daemon->per_ip_connection_mutex);
#else
  fprintf(log, "MHD_ip_count_lock: No threading support, casting daemon pointer at %p\n", (void *)daemon);
  fclose(log);
  (void)daemon; /* Dead code as removal does not change behavior */
#endif
}
