#include <stdio.h>
#include <stdbool.h>

static void log_status_code(unsigned int status_code, const char* location)
{
    FILE *fp = fopen("/tmp/logs/405.txt", "a");
    if (fp != NULL)
    {
        fprintf(fp, "%s: status_code = %u\n", location, status_code);
        fclose(fp);
    }
}

typedef enum { MHD_NO = 0, MHD_YES = 1 } MHD_Result;

struct MHD_Response {
    int fd;
    bool is_pipe;
    unsigned int flags;
    unsigned int flags_auto;
    void (*upgrade_handler)(void);
    struct MHD_HTTP_Res_Header *first_header;
    unsigned int total_size;
};

struct MHD_HTTP_Res_Header {
    const char* header;
    const char* value;
    struct MHD_HTTP_Res_Header* next;
};

struct MHD_Request {
    unsigned int http_ver;
    unsigned int http_mthd;
    unsigned int remaining_upload_size;
};

struct
{
    struct MHD_Response *response;
    unsigned int responseCode;
    bool responseIcy;
    void (*resp_sender)(void);
    unsigned int rsp_write_position;
} rp;

struct MHD_Connection {
    struct MHD_Daemon *daemon;
    bool in_access_handler;
    bool suspended;
    bool in_idle;
    bool discard_request;
    unsigned int state;
    int tid;
    struct MHD_Request rq;
    struct {
      struct MHD_Response *response;
      unsigned int responseCode;
      bool responseIcy;
      void (*resp_sender)(void);
      unsigned int rsp_write_position;
    } rp;
    int sk_spipe_suppress;
};

struct MHD_Daemon {
    unsigned int options;
    bool shutdown;
    bool sigpipe_blocked;
};

// Dummy constants and function stubs to allow compilation
#define MHD_ICY_FLAG 0x80000000
#define MHD_NO 0
#define MHD_YES 1
#define MHD_HTTP_SWITCHING_PROTOCOLS 101
#define MHD_HTTP_VER_1_0 0
#define MHD_HTTP_MTHD_HEAD 3
#define MHD_HTTP_OK 200
#define MHD_HTTP_NO_CONTENT 204
#define MHD_HTTP_NOT_MODIFIED 304
#define MHD_CONNECTION_HEADERS_PROCESSED 2
#define MHD_CONNECTION_FULL_REQ_RECEIVED 3
#define MHD_CONNECTION_START_REPLY 4
#define MHD_RF_HEAD_ONLY_RESPONSE 0x01
#define MHD_RF_HTTP_1_0_COMPATIBLE_STRICT 0x02
#define MHD_RF_HTTP_1_0_SERVER 0x04
#define MHD_RF_INSANITY_HEADER_CONTENT_LENGTH 0x08
#define MHD_RAF_HAS_CONTENT_LENGTH 0x10
#define MHD_RAF_HAS_CONNECTION_HDR 0x20
#define MHD_HTTP_HEADER_CONNECTION "Connection"
#define MHD_HTTP_MTHD_CONNECT 7
#define RP_BODY_HEADERS_ONLY 0
#define MHD_USE_POSIX_THREADS 1
#define MHD_USE_W32_THREADS 1
#define _MHD_HAVE_SENDFILE 1
#define MHD_USE_TLS 0

static inline bool MHD_D_IS_USING_THREADS_ (struct MHD_Daemon *daemon) { return true; }
static inline bool MHD_thread_handle_ID_is_current_thread_ (int tid) { return true; }
static inline void MHD_DLOG (struct MHD_Daemon *daemon, const char *msg, ...) { (void)daemon; (void)msg; }
static inline void mhd_assert(int expr) { (void)expr; }
static inline int MHD_str_equal_caseless_(const char *a, const char *b) { return (a && b) ? (strcmp(a,b) == 0) : 0; }
static inline int MHD_str_has_s_token_caseless_(const char *value, const char *token) { return (value && token) ? 1 : 0; }
static inline int is_reply_body_needed(struct MHD_Connection *connection, unsigned int status_code) { (void)connection; (void)status_code; return 1; }
static inline void MHD_increment_response_rc(struct MHD_Response *response) { (void)response; }
static inline void MHD_update_last_activity_(struct MHD_Connection *connection) { (void)connection; }
static inline int MHD_IS_HTTP_VER_1_1_COMPAT(unsigned int ver) { (void)ver; return 1; }
static inline int MHD_resp_sender_std(void) { return 0; }
static inline int MHD_resp_sender_sendfile(void) { return 0; }
static inline int MHD_connection_handle_idle (struct MHD_Connection *connection) { (void)connection; return 0; }

_MHD_EXTERN enum MHD_Result
MHD_queue_response (struct MHD_Connection *connection,
                    unsigned int status_code,
                    struct MHD_Response *response)
{
  struct MHD_Daemon *daemon;
  bool reply_icy;

  // Log initial parameter status_code
  log_status_code(status_code, "Entry");

  if ((NULL == connection) || (NULL == response))
    return MHD_NO;

  daemon = connection->daemon;
  if ((! connection->in_access_handler) && (! connection->suspended) &&
      MHD_D_IS_USING_THREADS_ (daemon))
    return MHD_NO;

  reply_icy = (0 != (status_code & MHD_ICY_FLAG));

  // Log before modifying status_code
  log_status_code(status_code, "Before modification");

  status_code &= ~MHD_ICY_FLAG;

  // Log after modifying status_code
  log_status_code(status_code, "After modification");

#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  if ( (! connection->suspended) &&
       MHD_D_IS_USING_THREADS_ (daemon) &&
       (! MHD_thread_handle_ID_is_current_thread_ (connection->tid)) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Attempted to queue response on wrong thread!\n"));
#endif
    return MHD_NO;
  }
#endif

  if (NULL != connection->rp.response)
    return MHD_NO; /* The response was already set */

  if ( (MHD_CONNECTION_HEADERS_PROCESSED != connection->state) &&
       (MHD_CONNECTION_FULL_REQ_RECEIVED != connection->state) )
    return MHD_NO; /* Wrong connection state */

  if (daemon->shutdown)
    return MHD_NO;

#ifdef UPGRADE_SUPPORT
  if (NULL != response->upgrade_handler)
  {
    struct MHD_HTTP_Res_Header *conn_header;
    if (0 == (daemon->options & MHD_ALLOW_UPGRADE))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Attempted 'upgrade' connection on daemon without" \
                   " MHD_ALLOW_UPGRADE option!\n"));
#endif
      return MHD_NO;
    }
    if (MHD_HTTP_SWITCHING_PROTOCOLS != status_code)
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Application used invalid status code for" \
                   " 'upgrade' response!\n"));
#endif
      return MHD_NO;
    }
    if (0 == (response->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Application used invalid response" \
                   " without \"Connection\" header!\n"));
#endif
      return MHD_NO;
    }
    conn_header = response->first_header;
    mhd_assert (NULL != conn_header);
    mhd_assert (MHD_str_equal_caseless_ (conn_header->header,
                                         MHD_HTTP_HEADER_CONNECTION));
    if (! MHD_str_has_s_token_caseless_ (conn_header->value,
                                         "upgrade"))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Application used invalid response" \
                   " without \"upgrade\" token in" \
                   " \"Connection\" header!\n"));
#endif
      return MHD_NO;
    }
    if (! MHD_IS_HTTP_VER_1_1_COMPAT (connection->rq.http_ver))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Connection \"Upgrade\" can be used only " \
                   "with HTTP/1.1 connections!\n"));
#endif
      return MHD_NO;
    }
  }
#endif /* UPGRADE_SUPPORT */
  if (MHD_HTTP_SWITCHING_PROTOCOLS == status_code)
  {
#ifdef UPGRADE_SUPPORT
    if (NULL == response->upgrade_handler)
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Application used status code 101 \"Switching Protocols\" " \
                   "with non-'upgrade' response!\n"));
#endif /* HAVE_MESSAGES */
      return MHD_NO;
    }
#else  /* ! UPGRADE_SUPPORT */
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Application used status code 101 \"Switching Protocols\", " \
                 "but this MHD was built without \"Upgrade\" support!\n"));
#endif /* HAVE_MESSAGES */
    return MHD_NO;
#endif /* ! UPGRADE_SUPPORT */
  }
  if ( (100 > status_code) ||
       (999 < status_code) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Refused wrong status code (%u). " \
                 "HTTP requires three digits status code!\n"),
              status_code);
#endif
    return MHD_NO;
  }
  if (200 > status_code)
  {
    if (MHD_HTTP_VER_1_0 == connection->rq.http_ver)
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Wrong status code (%u) refused. " \
                   "HTTP/1.0 clients do not support 1xx status codes!\n"),
                (status_code));
#endif
      return MHD_NO;
    }
    if (0 != (response->flags & (MHD_RF_HTTP_1_0_COMPATIBLE_STRICT
                                 | MHD_RF_HTTP_1_0_SERVER)))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Wrong status code (%u) refused. " \
                   "HTTP/1.0 reply mode does not support 1xx status codes!\n"),
                (status_code));
#endif
      return MHD_NO;
    }
  }
  if ( (MHD_HTTP_MTHD_CONNECT == connection->rq.http_mthd) &&
       (2 == status_code / 100) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Successful (%u) response code cannot be used to answer " \
                 "\"CONNECT\" request!\n"),
              (status_code));
#endif
    return MHD_NO;
  }

  if ( (0 != (MHD_RF_HEAD_ONLY_RESPONSE & response->flags)) &&
       (RP_BODY_HEADERS_ONLY < is_reply_body_needed (connection, status_code)) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("HEAD-only response cannot be used when the request requires "
                 "reply body to be sent!\n"));
#endif
    return MHD_NO;
  }

#ifdef HAVE_MESSAGES
  if ( (0 != (MHD_RF_INSANITY_HEADER_CONTENT_LENGTH & response->flags)) &&
       (0 != (MHD_RAF_HAS_CONTENT_LENGTH & response->flags_auto)) )
  {
    MHD_DLOG (daemon,
              _ ("The response has application-defined \"Content-Length\" " \
                 "header. The reply to the request will be not " \
                 "HTTP-compliant and may result in hung connection or " \
                 "other problems!\n"));
  }
#endif

  MHD_increment_response_rc (response);
  connection->rp.response = response;
  connection->rp.responseCode = status_code;
  connection->rp.responseIcy = reply_icy;
#if defined(_MHD_HAVE_SENDFILE)
  if ( (response->fd == -1) ||
       (response->is_pipe) ||
       (0 != (connection->daemon->options & MHD_USE_TLS))
#if defined(MHD_SEND_SPIPE_SUPPRESS_NEEDED) && \
       defined(MHD_SEND_SPIPE_SUPPRESS_POSSIBLE)
       || (! daemon->sigpipe_blocked && ! connection->sk_spipe_suppress)
#endif /* MHD_SEND_SPIPE_SUPPRESS_NEEDED &&
          MHD_SEND_SPIPE_SUPPRESS_POSSIBLE */
       )
    connection->rp.resp_sender = MHD_resp_sender_std;
  else
    connection->rp.resp_sender = MHD_resp_sender_sendfile;
#endif /* _MHD_HAVE_SENDFILE */

  if ( (MHD_HTTP_MTHD_HEAD == connection->rq.http_mthd) ||
       (MHD_HTTP_OK > status_code) ||
       (MHD_HTTP_NO_CONTENT == status_code) ||
       (MHD_HTTP_NOT_MODIFIED == status_code) )
  {
    connection->rp.rsp_write_position = response->total_size;
  }
  if (MHD_CONNECTION_HEADERS_PROCESSED == connection->state)
  {
    connection->discard_request = true;
    connection->state = MHD_CONNECTION_START_REPLY;
    connection->rq.remaining_upload_size = 0;
  }
  if (! connection->in_idle)
    (void) MHD_connection_handle_idle (connection);
  MHD_update_last_activity_ (connection);

  // Log final status_code value before return
  log_status_code(status_code, "Exit");

  return MHD_YES;
}

// Dummy _MHD_EXTERN definition
#define _MHD_EXTERN
