void log_state(const char *msg)
{
    FILE *fp = fopen("/tmp/logs/407.txt", "a");
    if (fp)
    {
        fprintf(fp, "%s\n", msg);
        fclose(fp);
    }
}
int MHD_str_has_s_token_caseless_(const char *str, const char *tok)
{
    log_state("Called MHD_str_has_s_token_caseless_");
    if (str == NULL || tok == NULL)
    {
        log_state("Null pointer passed to MHD_str_has_s_token_caseless_");
        return 0;
    }

    const char *p = str;
    size_t tok_len = strlen(tok);

    while (*p != '\0')
    {
        size_t i;
        for (i = 0; i < tok_len; i++)
        {
            if ((unsigned char)(p[i] | 0x20) != (unsigned char)(tok[i] | 0x20))
                break;
        }
        if (i == tok_len)
        {
            log_state("Token found in MHD_str_has_s_token_caseless_");
            return 1;
        }
        p++;
    }
    log_state("Token not found in MHD_str_has_s_token_caseless_");
    return 0;
}
