struct MHD_ResponseProps {
    struct MHD_Response *response;
    unsigned int responseCode;
    bool responseIcy;
    unsigned long rsp_write_position;
    void *resp_sender;
};
static inline bool MHD_D_IS_USING_THREADS_(void *d) { return false; }
static inline bool MHD_thread_handle_ID_is_current_thread_(unsigned int tid) { return true; }
