#include <stdio.h>
#include <string.h>

_MHD_EXTERN enum MHD_Result
MHD_set_connection_value_n(struct MHD_Connection *connection,
                          enum MHD_ValueKind kind,
                          const char *key,
                          size_t key_size,
                          const char *value,
                          size_t value_size)
{
  FILE *log = fopen("/tmp/logs/439.txt", "a");
  if (log == NULL)
  {
    /* Cannot open log file, continue without logging */
  }

  if (log)
  {
    fprintf(log, "Entry: kind=%d, key=0x%p, key_size=%zu, value=0x%p, value_size=%zu\n",
            kind, (void*)key, key_size, (void*)value, value_size);
  }

  if ((MHD_GET_ARGUMENT_KIND != kind) &&
      ((((key ? strlen(key) : 0)) != key_size) ||
       (((value ? strlen(value) : 0)) != value_size)))
  {
    if (log)
    {
      fprintf(log, "Violation detected: strlen(key) or strlen(value) does not match sizes\n");
      fclose(log);
    }
    return MHD_NO; /* binary zero is allowed only in GET arguments */
  }

  if (log)
  {
    fprintf(log, "No violation detected: calling MHD_set_connection_value_n_nocheck_\n");
    fclose(log);
  }

  return MHD_set_connection_value_n_nocheck_(connection, kind, key, key_size, value, value_size);
}
