extern void MHD_PANIC(const char *message);
void assert_mhd(bool condition)
{
    if (!condition)
    {
        FILE *f = fopen("/tmp/logs/384.txt", "a");
        if(f != NULL)
        {
            fprintf(f, "Assertion failed\n");
            fclose(f);
        }
        MHD_PANIC("Assertion failed");
    }
}

extern void MHD_stop_daemon(struct MHD_Daemon *daemon)
{
    FILE *logf = fopen("/tmp/logs/384.txt", "a");
    if(logf)
        fprintf(logf, "Entered MHD_stop_daemon\n");
    if (NULL == daemon)
    {
        if(logf) fprintf(logf, "daemon is NULL, return immediately\n");
        if(logf) fclose(logf);
        return;
    }
    if(logf) fprintf(logf, "daemon is not NULL, shutdown flag=%d\n", daemon->shutdown);

    if ( (daemon->shutdown) && (NULL == daemon->master) )
    {
        if(logf) fprintf(logf, "Detected double call to MHD_stop_daemon, panicking\n");
        if(logf) fclose(logf);
        MHD_PANIC ("MHD_stop_daemon() was called twice.");
    }
    if(logf) fprintf(logf, "Setting daemon shutdown to true\n");
    daemon->shutdown = true;
    if(logf) fclose(logf);
}

