#include <stdio.h>
#include <string.h>
#include <errno.h>
#include <stdlib.h>
#include <sys/stat.h>
#include <dirent.h>
#include <unistd.h>

extern const char * MHD_HTTP_METHOD_GET;
extern enum MHD_Result MHD_YES;
extern enum MHD_Result MHD_NO;
extern enum MHD_Result MHD_HTTP_SERVICE_UNAVAILABLE;
extern enum MHD_Result MHD_HTTP_OK;
extern struct MHD_Response * MHD_create_response_from_buffer (size_t size, void *buffer, int mode);
extern enum MHD_Result MHD_queue_response (void *connection, unsigned int status_code, struct MHD_Response *response);
extern void MHD_destroy_response (struct MHD_Response *response);
extern enum MHD_Result MHD_create_response_from_callback (size_t size, size_t page_size, void *reader_func, void *cls, void *free_func);

// Minimal stubs to compile
#define MHD_RESPMEM_MUST_COPY 1
#define MHD_HTTP_METHOD_GET "GET"
#define MHD_YES 1
#define MHD_NO 0
#define MHD_HTTP_SERVICE_UNAVAILABLE 503
#define MHD_HTTP_OK 200

// Define enum for MHD_Result as int for example purposes
typedef int enum_MHD_Result;

// Dummy structures for compilation
struct MHD_Connection { int dummy; };
struct MHD_Response { int dummy; };

static int aptr;

static enum_MHD_Result
ahc_echo (void *cls,
          struct MHD_Connection *connection,
          const char *url,
          const char *method,
          const char *version,
          const char *upload_data,
          size_t *upload_data_size, void **req_cls)
{
  static FILE *logfile = NULL;
  if (logfile == NULL) {
    logfile = fopen("/tmp/logs/471.txt", "w");
    if (logfile == NULL) { return MHD_NO; }
  }
  // Log initial parameters
  fprintf(logfile, "ahc_echo called: method='%s', url='%s'\n", method ? method : "(null)", url ? url : "(null)");

  struct MHD_Response *response;
  enum_MHD_Result ret;
  FILE *file;
  int fd;
  DIR *dir;
  struct stat buf;
  char emsg[1024];

  (void) cls;
  (void) version;
  (void) upload_data;
  (void) upload_data_size;

  if (0 != strcmp(method, MHD_HTTP_METHOD_GET))
  {
    fprintf(logfile, "Method is not GET. Returning MHD_NO.\n");
    fflush(logfile);
    return MHD_NO;              /* unexpected method */
  }

  if (&aptr != *req_cls)
  {
    *req_cls = &aptr;
    fprintf(logfile, "First call, returning MHD_YES.\n");
    fflush(logfile);
    return MHD_YES;
  }
  *req_cls = NULL;

  // Log url length and content
  if (url == NULL)
  {
    fprintf(logfile, "URL is NULL, returning MHD_NO.\n");
    fflush(logfile);
    return MHD_NO;
  }
  size_t url_len = strlen(url);
  fprintf(logfile, "URL length: %zu\n", url_len);

  // The potential violation: fopen(&url[1])
  if (url_len < 1)
  {
    fprintf(logfile, "URL length is less than 1, &url[1] points out of bounds!\n");
    fflush(logfile);
  }
  // Log the string used for fopen
  fprintf(logfile, "Calling fopen with filename: '%s'\n", url_len >= 1 ? &url[1] : "(out-of-bounds)");
  fflush(logfile);

  file = fopen(&url[1], "rb");
  if (NULL != file)
  {
    fd = fileno(file);
    if (-1 == fd)
    {
      fprintf(logfile, "fileno failed. Closing file and returning MHD_NO.\n");
      (void) fclose (file);
      fflush(logfile);
      return MHD_NO;     /* internal error */
    }
    if ( (0 != fstat(fd, &buf)) || (! S_ISREG(buf.st_mode)) )
    {
      fprintf(logfile, "File is not a regular file. Closing file and setting file=NULL.\n");
      fclose(file);
      file = NULL;
      fflush(logfile);
    }
  }

  if (NULL == file)
  {
    dir = opendir (".");
    if (NULL == dir)
    {
      snprintf (emsg, sizeof(emsg),"Failed to open directory `.': %s\n", strerror(errno));
      fprintf(logfile, "%s", emsg);
      fflush(logfile);
      response = MHD_create_response_from_buffer(strlen(emsg), emsg, MHD_RESPMEM_MUST_COPY);
      if (NULL == response)
      {
        fprintf(logfile, "Failed to create response from buffer.\n");
        fflush(logfile);
        return MHD_NO;
      }
      ret = MHD_queue_response(connection, MHD_HTTP_SERVICE_UNAVAILABLE, response);
      MHD_destroy_response(response);
    }
    else
    {
      response = MHD_create_response_from_callback((size_t)-1, 32 * 1024, NULL, dir, NULL);
      if (NULL == response)
      {
        closedir(dir);
        fprintf(logfile, "Failed to create response from callback for directory.\n");
        fflush(logfile);
        return MHD_NO;
      }
      ret = MHD_queue_response(connection, MHD_HTTP_OK, response);
      MHD_destroy_response(response);
    }
  }
  else
  {
    response = MHD_create_response_from_callback((size_t) buf.st_size, 32 * 1024, NULL, file, NULL);
    if (NULL == response)
    {
      fclose(file);
      fprintf(logfile, "Failed to create response from callback for file.\n");
      fflush(logfile);
      return MHD_NO;
    }
    ret = MHD_queue_response(connection, MHD_HTTP_OK, response);
    MHD_destroy_response(response);
  }
  fprintf(logfile, "Returning ret = %d from ahc_echo.\n", ret);
  fflush(logfile);
  return ret;
}
