static enum MHD_Result
compress_buf (z_stream *strm, const void *src, size_t src_size, size_t *offset,
              void **dest, size_t *dest_size,
              void *tmp)
{
  FILE *log = fopen("/tmp/logs/714.txt", "a");
  unsigned int have;
  enum MHD_Result ret;
  int flush;
  void *tmp_dest;

  *dest = NULL;
  *dest_size = 0;

  if (log) fprintf(log, "compress_buf start: src_size=%zu\n", src_size);

  do
  {
    if (src_size > CHUNK)
    {
      strm->avail_in = CHUNK;
      src_size -= CHUNK;
      flush = Z_NO_FLUSH;
      if (log) fprintf(log, "Chunk > CHUNK: avail_in=%u, remaining src_size=%zu, flush=Z_NO_FLUSH\n", strm->avail_in, src_size);
    }
    else
    {
      strm->avail_in = (uInt) src_size;
      flush = Z_SYNC_FLUSH;
      if (log) fprintf(log, "Last chunk: avail_in=%u, flush=Z_SYNC_FLUSH\n", strm->avail_in);
    }

    *offset += strm->avail_in;
    strm->next_in = (const Bytef *) src;

    do
    {
      strm->avail_out = CHUNK;
      strm->next_out = tmp;
      ret = (Z_OK == deflate (strm, flush)) ? MHD_YES : MHD_NO;
      have = CHUNK - strm->avail_out;

      if (log) fprintf(log, "Inner loop: ret=%d, have=%u, dest_size_before=%zu\n", (int)ret, have, *dest_size);

      *dest_size += have;
      tmp_dest = realloc (*dest, *dest_size);
      if (NULL == tmp_dest)
      {
        free (*dest);
        *dest = NULL;
        if (log) fprintf(log, "Failed realloc, exiting\n");
        if (log) fclose(log);
        return MHD_NO;
      }
      *dest = tmp_dest;
      memcpy (((uint8_t *) (*dest)) + ((*dest_size) - have), tmp, have);

      if (log) fprintf(log, "Copied %u bytes to dest, dest_size_after=%zu\n", have, *dest_size);
    }
    while (0 == strm->avail_out);

  }
  while (flush != Z_SYNC_FLUSH);

  if (log) fprintf(log, "compress_buf end: ret=%d, total dest_size=%zu\n", (int)ret, *dest_size);
  if (log) fclose(log);

  return ret;
}
