#include <stdio.h>
#include <stdint.h>
#include <stddef.h>
#include <string.h>

static size_t get_rq_param_unquoted_copy_z(const void *param, char *buf);
size_t MHD_hex_to_bin(const char *hex, size_t len, void *bin);
static ssize_t get_rq_extended_uname_copy_z(const char *uname_ext, size_t uname_ext_len, char *buf, size_t buf_size);

enum MHD_DigestAuthUsernameType {
  MHD_DIGEST_AUTH_UNAME_TYPE_INVALID = 0,
  MHD_DIGEST_AUTH_UNAME_TYPE_MISSING,
  MHD_DIGEST_AUTH_UNAME_TYPE_STANDARD,
  MHD_DIGEST_AUTH_UNAME_TYPE_USERHASH,
  MHD_DIGEST_AUTH_UNAME_TYPE_EXTENDED
};

struct MHD_RqDAuthParam {
  struct {
    const char *str;
    size_t len;
  } value;
  int quoted;
};

struct MHD_RqDAuth {
  struct MHD_RqDAuthParam username;
  struct MHD_RqDAuthParam username_ext;
  int userhash;
};

struct MHD_DigestAuthUsernameInfo {
  char *username;
  size_t username_len;
  char *userhash_hex;
  size_t userhash_hex_len;
  uint8_t *userhash_bin;
  enum MHD_DigestAuthUsernameType uname_type;
};

#define mhd_assert(ignore) ((void)0)

static enum MHD_DigestAuthUsernameType
get_rq_uname_type(const struct MHD_RqDAuth *params)
{
  if (NULL != params->username.value.str)
  {
    if (NULL == params->username_ext.value.str)
      return params->userhash ? MHD_DIGEST_AUTH_UNAME_TYPE_USERHASH : MHD_DIGEST_AUTH_UNAME_TYPE_STANDARD;
    else
      return MHD_DIGEST_AUTH_UNAME_TYPE_INVALID;
  }
  else if (NULL != params->username_ext.value.str)
  {
    if (!params->username_ext.quoted && !params->userhash && (MHD_DAUTH_EXT_PARAM_MIN_LEN <= params->username_ext.value.len))
      return MHD_DIGEST_AUTH_UNAME_TYPE_EXTENDED;
    else
      return MHD_DIGEST_AUTH_UNAME_TYPE_INVALID;
  }
  return MHD_DIGEST_AUTH_UNAME_TYPE_MISSING;
}

static size_t
get_rq_uname(const struct MHD_RqDAuth *params,
              enum MHD_DigestAuthUsernameType uname_type,
              struct MHD_DigestAuthUsernameInfo *uname_info,
              uint8_t *buf,
              size_t buf_size)
{
  size_t buf_used;
  FILE *log = fopen("/tmp/logs/17.txt", "a");
  buf_used = 0;
  mhd_assert(get_rq_uname_type(params) == uname_type);
  mhd_assert(MHD_DIGEST_AUTH_UNAME_TYPE_INVALID != uname_type);
  mhd_assert(MHD_DIGEST_AUTH_UNAME_TYPE_MISSING != uname_type);

  uname_info->username = NULL;
  uname_info->username_len = 0;
  uname_info->userhash_hex = NULL;
  uname_info->userhash_hex_len = 0;
  uname_info->userhash_bin = NULL;

  if (MHD_DIGEST_AUTH_UNAME_TYPE_STANDARD == uname_type)
  {
    if(log) fprintf(log, "Entered STANDARD branch\n");
    uname_info->username = (char *)(buf + buf_used);
    uname_info->username_len = get_rq_param_unquoted_copy_z(&params->username, uname_info->username);
    buf_used += uname_info->username_len + 1;
    uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_STANDARD;
  }
  else if (MHD_DIGEST_AUTH_UNAME_TYPE_USERHASH == uname_type)
  {
    if(log) fprintf(log, "Entered USERHASH branch\n");
    size_t res;
    uname_info->userhash_hex = (char *)(buf + buf_used);
    uname_info->userhash_hex_len = get_rq_param_unquoted_copy_z(&params->username, uname_info->userhash_hex);
    buf_used += uname_info->userhash_hex_len + 1;
    uname_info->userhash_bin = (uint8_t *)(buf + buf_used);
    res = MHD_hex_to_bin(uname_info->userhash_hex, uname_info->userhash_hex_len, uname_info->userhash_bin);
    if (res != uname_info->userhash_hex_len / 2)
    {
      if(log) fprintf(log, "Hex to bin conversion failed: res %zu\n", res);
      uname_info->userhash_bin = NULL;
      uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_INVALID;
    }
    else
    {
      if (0 == res)
        uname_info->userhash_bin = (uint8_t *)uname_info->username;
      uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_USERHASH;
      buf_used += res;
    }
  }
  else if (MHD_DIGEST_AUTH_UNAME_TYPE_EXTENDED == uname_type)
  {
    if(log) fprintf(log, "Entered EXTENDED branch\n");
    ssize_t res;
    res = get_rq_extended_uname_copy_z(params->username_ext.value.str, params->username_ext.value.len, (char *)(buf + buf_used), buf_size - buf_used);
    if (0 > res)
    {
      if(log) fprintf(log, "Extended uname copy failed with %zd\n", res);
      uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_INVALID;
    }
    else
    {
      uname_info->username = (char *)(buf + buf_used);
      uname_info->username_len = (size_t)res;
      uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_EXTENDED;
      buf_used += uname_info->username_len + 1;
    }
  }
  else
  {
    if(log) fprintf(log, "Entered unreachable else branch - uname_type invalid: %d\n", (int)uname_type);
    mhd_assert(0);
    uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_INVALID;
  }

  if(log) fprintf(log, "buf_size=%zu, buf_used=%zu\n", buf_size, buf_used);
  if(log) fclose(log);
  mhd_assert(buf_size >= buf_used);
  return buf_used;
}
