static int
mhd_tool_get_proc_cpu_count_sched_getaffinity_np_ (void)
{
  int ret = -1;
#if defined(HAVE_SCHED_GETAFFINITY_NP) && defined(HAVE_GETPID)
  FILE *log_file = fopen("/tmp/logs/164.txt", "w");
  cpuset_t *cpuset_ptr;
  cpuset_ptr = cpuset_create ();
  if (NULL != cpuset_ptr)
  {
    if (0 == sched_getaffinity_np (getpid (), cpuset_size (cpuset_ptr), cpuset_ptr))
    {
      cpuid_t cpu_num;
#if defined(HAVE_SYSCONF) && defined(HAVE_DECL__SC_NPROCESSORS_CONF)
      unsigned int max_num = 0;
      long sc_value;
      sc_value = sysconf (_SC_NPROCESSORS_ONLN);
      if (0 < sc_value)
        max_num = (unsigned int) sc_value;
      if (0 < max_num)
      {
        ret = 0;
        fprintf(log_file, "Entering for loop with max_num=%u\n", max_num);
        for (cpu_num = 0; cpu_num < max_num; ++cpu_num)
        {
          fprintf(log_file, "for loop iteration cpu_num=%u\n", cpu_num);
          if (0 < cpuset_isset (cpu_num, cpuset_ptr))
            ++ret;
        }
        fprintf(log_file, "Exited for loop with ret=%d\n", ret);
      }
      else /* Combined with the next 'if' */
#endif /* HAVE_SYSCONF && HAVE_DECL__SC_NPROCESSORS_CONF */
      if (1)
      {
        int res;
        /* Violation scenario: cpu_num is incremented in loop body via cpuset_isset(cpu_num++) */
        cpu_num = 0;
        ret = 0;
        fprintf(log_file, "Starting do-while loop with cpu_num=0\n");
        do
        {
          res = cpuset_isset (cpu_num++, cpuset_ptr);
          fprintf(log_file, "do-while loop cpu_num incremented to %u, res=%d\n", cpu_num, res);
          if (0 < res)
            ++ret;
        } while (0 <= res);
        fprintf(log_file, "Exited do-while loop with ret=%d and cpu_num=%u\n", ret, cpu_num);
      }
#ifdef __NetBSD__
      if (0 == ret)
      {
        /* On NetBSD "unset" affinity (exactly zero CPUs) means
           "all CPUs are available". */
        ret = mhd_tool_get_sys_cpu_count_sysctl_ ();
        fprintf(log_file, "NetBSD fallback: ret=%d\n", ret);
      }
#endif /* __NetBSD__ */
    }
    cpuset_destroy (cpuset_ptr);
  }
  if (log_file != NULL)
  {
    fclose(log_file);
  }
#endif /* HAVE_SCHED_GETAFFINITY_NP && HAVE_GETPID */
  if (0 >= ret)
    return -1;
  return ret;
}
