int
mhd_tool_get_system_cpu_count (void)
{
  int res;
  FILE *logf = fopen("/tmp/logs/250.txt", "w");

  /* Try specialised APIs first */
  res = mhd_tool_get_sys_cpu_count_special_api_ ();
  fprintf(logf, "special_api returned: %d\n", res);
  if (0 < res)
  {
    fprintf(logf, "Returning early from special_api: %d\n", res);
    fclose(logf);
    return res;
  }

  /* Try sysctl*(). This is typically a direct interface to kernel values. */
  res = mhd_tool_get_sys_cpu_count_sysctl_ ();
  fprintf(logf, "sysctl returned: %d\n", res);
  if (0 < res)
  {
    fprintf(logf, "Returning early from sysctl: %d\n", res);
    fclose(logf);
    return res;
  }

  /* Try sysconf() as the last resort as this is a generic interface which can be implemented by parsing system files. */
  res = mhd_tool_get_sys_cpu_count_sysconf_ ();
  fprintf(logf, "sysconf returned: %d\n", res);
#if ! defined(__linux__) && ! defined(__GLIBC__)
  if (0 < res)
  {
    fprintf(logf, "Returning early from sysconf: %d\n", res);
    fclose(logf);
    return res;
  }
#else  /* __linux__ || __GLIBC__ */
  if (2 < res)
  {
    fprintf(logf, "Returning early from sysconf (> 2): %d\n", res);
    fclose(logf);
    return res;
  }
  if (0 < res)
  {
    /* '1' or '2' could a be fallback number. See get_nprocs_fallback() in glibc sysdeps/unix/sysv/linux/getsysstats.c */
    int proc_cpu_count;

    proc_cpu_count = mhd_tool_get_proc_cpu_count ();
    fprintf(logf, "proc_cpu_count: %d, sysconf res: %d\n", proc_cpu_count, res);
    if (proc_cpu_count == res)
    {
      fprintf(logf, "proc_cpu_count equals sysconf res, returning: %d\n", res);
      fclose(logf);
      return res;
    }
    else
    {
      /* Here the call to proc_cpu_count and comparison produces no effect on output */
      fprintf(logf, "proc_cpu_count not equal to sysconf res, continuing\n");
    }
  }
#endif /* __linux__ || __GLIBC__  */

  /* Try available fallbacks */
  res = mhd_tool_get_sys_cpu_count_sysctl_fallback_ ();
  fprintf(logf, "sysctl_fallback returned: %d\n", res);
  if (0 < res)
  {
    fprintf(logf, "Returning early from sysctl_fallback: %d\n", res);
    fclose(logf);
    return res;
  }

  res = mhd_tool_get_sys_cpu_count_sysconf_fallback_ ();
  fprintf(logf, "sysconf_fallback returned: %d\n", res);
#if ! defined(__linux__) && ! defined(__GLIBC__)
  if (0 < res)
  {
    fprintf(logf, "Returning early from sysconf_fallback: %d\n", res);
    fclose(logf);
    return res;
  }
#else  /* __linux__ || __GLIBC__ */
  if (2 < res)
  {
    fprintf(logf, "Returning early from sysconf_fallback (> 2): %d\n", res);
    fclose(logf);
    return res;
  }
#endif /* __linux__ || __GLIBC__  */

  fprintf(logf, "Cannot detect CPU count, returning -1\n");
  fclose(logf);
  return -1; /* Cannot detect */
}
