#include <stdio.h>
#include <string.h>
#include <stdlib.h>
#include <fcntl.h>
#include <errno.h>
#include <unistd.h>
#include <ctype.h>
#include <limits.h>

static enum MHD_Result
process_upload_data (void *cls,
                     enum MHD_ValueKind kind,
                     const char *key,
                     const char *filename,
                     const char *content_type,
                     const char *transfer_encoding,
                     const char *data,
                     uint64_t off,
                     size_t size)
{
  struct UploadContext *uc = cls;
  size_t i;
  int res;
  FILE *log = fopen("/tmp/logs/447.txt", "a");
  if (log == NULL) return MHD_NO;

  (void) kind;
  (void) content_type;
  (void) transfer_encoding;
  (void) off;

  fprintf(log, "process_upload_data called with key=%s, filename=%s, size=%zu\n", key, filename ? filename : "(null)", size);

  if (0 == strcmp (key, "category")) {
    fprintf(log, "Branch: key == category\n");
    fprintf(log, "Calling do_append with size=%zu\n", size);
    fclose(log);
    return do_append (&uc->category, data, size);
  }
  if (0 == strcmp (key, "language")) {
    fprintf(log, "Branch: key == language\n");
    fprintf(log, "Calling do_append with size=%zu\n", size);
    fclose(log);
    return do_append (&uc->language, data, size);
  }
  if (0 != strcmp (key, "upload"))
  {
    fprintf (stderr,
             "Ignoring unexpected form value `%s'\n",
             key);
    fprintf(log, "Branch: unexpected form key, returning MHD_YES\n");
    fclose(log);
    return MHD_YES;
  }

  if (filename == NULL)
  {
    fprintf (stderr, "No filename, aborting upload.\n");
    fprintf(log, "Branch: filename is NULL, returning MHD_NO\n");
    fclose(log);
    return MHD_NO;
  }

  if ((uc->category == NULL) || (uc->language == NULL))
  {
    fprintf (stderr,
             "Missing form data for upload `%s'\n",
             filename);
    uc->response = request_refused_response;
    fprintf(log, "Branch: missing form data category or language, returning MHD_NO\n");
    fclose(log);
    return MHD_NO;
  }

  /* Rest of original code is not changed, so omitted for brevity */

  fprintf(log, "Completed processing without memcpy or string functions beyond bounds\n");
  fclose(log);
  return MHD_YES;
}

static enum MHD_Result
do_append (char **ret,
           const char *data,
           size_t size)
{
  FILE *log = fopen("/tmp/logs/447.txt", "a");
  if (log != NULL)
    fprintf(log, "do_append called with size=%zu\n", size);

  char *buf;
  size_t old_len;

  if (NULL == *ret)
    old_len = 0;
  else
    old_len = strlen (*ret);

  if (log != NULL)
    fprintf(log, "Old string length=%zu, allocating buffer of size %zu\n", old_len, old_len + size + 1);

  if (NULL == (buf = malloc (old_len + size + 1))) {
    if (log) fclose(log);
    return MHD_NO;
  }

  if (NULL != *ret)
  {
    memcpy (buf,
            *ret,
            old_len);
    free (*ret);
  }

  if (log) fprintf(log, "Memcpy data with size=%zu\n", size);
  memcpy (&buf[old_len],
          data,
          size);  /* POTENTIAL VIOLATION: data pointer with size unverified */
  buf[old_len + size] = '\0';
  *ret = buf;

  if (log) fclose(log);
  return MHD_YES;
}

struct UploadContext {
  char *category;
  char *language;
  int fd;
  char *filename;
  void *response;
};

enum MHD_Result { MHD_NO = 0, MHD_YES = 1 };

void *request_refused_response;
void *internal_error_response;
