#include <stdio.h>
#include <string.h>
#include <stdbool.h>

#define XBUF_SIZE 2

struct MHD_PostProcessor
{
  char xbuf[2];
  size_t xbuf_pos;
  bool must_ikvi;
  uint64_t value_offset;
  enum {PP_Error} state;
  int (*ikvi)(void *cls, int kind, const void *key, void *unused1, void *unused2, void *unused3, char *data, uint64_t offset, size_t size);
  void *cls;
};

static void
process_value (struct MHD_PostProcessor *pp,
               const char *value_start,
               const char *value_end,
               const char *last_escape)
{
  FILE *log = fopen("/tmp/logs/695.txt", "a");
  if (log == NULL) return;

  char xbuf[XBUF_SIZE + 1];
  size_t xoff;

  fprintf(log, "start process_value\n");
  fprintf(log, "pp->xbuf_pos before: %zu\n", pp->xbuf_pos);
  if (0 != pp->xbuf_pos)
    {
      fprintf(log, "Calling memcpy(xbuf, pp->xbuf, pp->xbuf_pos) with pp->xbuf_pos=%zu\n", pp->xbuf_pos);
      memcpy (xbuf, pp->xbuf, pp->xbuf_pos);
    }
  xoff = pp->xbuf_pos;
  pp->xbuf_pos = 0;

  if ( (NULL != last_escape) && (((size_t) (value_end - last_escape)) < sizeof (pp->xbuf)) )
  {
    fprintf(log, "Safe: (value_end - last_escape)=%zu < sizeof(pp->xbuf)\n", (size_t)(value_end - last_escape));
    pp->xbuf_pos = (size_t) (value_end - last_escape);
    memcpy (pp->xbuf, last_escape, (size_t) (value_end - last_escape));
    value_end = last_escape;
  }
  else if (NULL != last_escape)
  {
    fprintf(log, "Violation case: (value_end - last_escape)=%zu >= sizeof(pp->xbuf)=%zu\n", (size_t)(value_end - last_escape), sizeof(pp->xbuf));
    /* Demonstrate the violation by calling memcpy with overflow size */
    memcpy (pp->xbuf, last_escape, (size_t) (value_end - last_escape)); /* This is a violation */
  }
  fclose(log);
}
