static void log_state(const char* stage, ssize_t ret, size_t size_to_fill, uint64_t left_to_send, int crc_null) {
  FILE *log = fopen("/tmp/logs/761.txt", "a");
  if (log) {
    fprintf(log, "Stage: %s\n", stage);
    fprintf(log, "ret: %zd, size_to_fill: %zu, left_to_send: %llu, crc_null: %d\n",
