#include <stdio.h>
#include <stdbool.h>
#include <stddef.h>
#include <stdint.h>
#include <string.h>
#include <assert.h>

/* Mock definitions to make the code compilable and executable for probe logging */

#define MHD_CONTENT_READER_END_OF_STREAM -1
#define MHD_CONTENT_READER_END_WITH_ERROR -2
#define MHD_NO 0
#define MHD_YES 1
#define MHD_CONNECTION_CHUNKED_BODY_UNREADY 2

#define MHD_USE_THREADS

/* Simplified assertion macro */
#define mhd_assert(x) assert(x)

/* Logging probe function to log control flow and state with line info */
static FILE *log_fp = NULL;
static void log_probe(const char *msg, int line)
{
    if (log_fp == NULL)
    {
        log_fp = fopen("/tmp/logs/762.txt", "a");
    }
    if (log_fp != NULL)
    {
        fprintf(log_fp, "Line %d: %s\n", line, msg);
        fflush(log_fp);
    }
}

struct MHD_Response
{
    void *crc_cls;
    ssize_t (*crc)(void *cls, size_t pos, void *buf, size_t len);
    unsigned char *data;
    size_t data_start;
    size_t data_size;
    size_t total_size;
    void *mutex;
};

struct MHD_Connection_RP
{
    size_t rsp_write_position;
};

struct MHD_Connection
{
    struct MHD_Response *rp_response;
    struct MHD_Connection_RP rp;
    char *write_buffer;
    size_t write_buffer_size;
    size_t write_buffer_append_offset;
    size_t write_buffer_send_offset;
    int state;
};

/* Dummy implementations of used functions and macros */
#define CONNECTION_CLOSE_ERROR(conn, msg) do { log_probe("CONNECTION_CLOSE_ERROR invoked", __LINE__); } while(0)

#define MHD_mutex_unlock_chk_(mut) do { log_probe("Mutex unlock", __LINE__); } while(0)

size_t MHD_uint32_to_strx(uint32_t val, char *buf, size_t buf_size)
{
    static const char hex_chars[] = "0123456789ABCDEF";
    size_t len = 0;
    int started = 0;
    for (int i = 28; i >= 0; i -= 4)
    {
        int digit = (val >> i) & 0xF;
        if (digit != 0 || started || i == 0)
        {
            if (len >= buf_size) return 0;
            buf[len++] = hex_chars[digit];
            started = 1;
        }
    }
    return len;
}

static enum MHD_Result
try_ready_chunked_body (struct MHD_Connection *connection,
                        bool *p_finished)
{
  ssize_t ret;
  struct MHD_Response *response;
  static const size_t max_chunk = 0xFFFFFF;
  char chunk_hdr[6];            /* 6: max strlen of "FFFFFF" */
  /* "FFFFFF" + "\r\n" */
  static const size_t max_chunk_hdr_len = sizeof(chunk_hdr) + 2;
  /* "FFFFFF" + "\r\n" + "\r\n" (chunk termination) */
  static const size_t max_chunk_overhead = sizeof(chunk_hdr) + 2 + 2;
  size_t chunk_hdr_len;
  uint64_t left_to_send;
  size_t size_to_fill;

  response = connection->rp_response;
  mhd_assert (NULL != response->crc || NULL != response->data);

  mhd_assert (0 == connection->write_buffer_append_offset);

  if (MHD_SIZE_UNKNOWN == response->total_size)
    left_to_send = MHD_SIZE_UNKNOWN;
  else
    left_to_send = response->total_size
                   - connection->rp.rsp_write_position;

  size_to_fill = connection->write_buffer_size - max_chunk_overhead;
  if (max_chunk < size_to_fill)
    size_to_fill = max_chunk;
  if (left_to_send < size_to_fill)
    size_to_fill = (size_t) left_to_send;

  if (0 == left_to_send)
    ret = MHD_CONTENT_READER_END_OF_STREAM;
  else if ((response->data_start <= connection->rp.rsp_write_position) &&
           (response->data_start + response->data_size > connection->rp.rsp_write_position))
  {
    const size_t data_write_offset = (size_t) (connection->rp.rsp_write_position - response->data_start);
    mhd_assert (SSIZE_MAX >= (response->data_size - data_write_offset));
    mhd_assert (response->data_size >= data_write_offset);
    ret = (ssize_t) (response->data_size - data_write_offset);
    if (((size_t) ret) > size_to_fill)
      ret = (ssize_t) size_to_fill;
    memcpy (&connection->write_buffer[max_chunk_hdr_len], &response->data[data_write_offset], (size_t) ret);
  }
  else
  {
    if (NULL == response->crc)
    { /* There is no way to reach this code */
      log_probe("Dead code block reached: response->crc is NULL", __LINE__);
#if defined(MHD_USE_THREADS)
      MHD_mutex_unlock_chk_ (&response->mutex);
#endif
      CONNECTION_CLOSE_ERROR (connection, "No callback for the chunked data.");
      return MHD_NO;
    }
    ret = response->crc (response->crc_cls, connection->rp.rsp_write_position, &connection->write_buffer[max_chunk_hdr_len], size_to_fill);
  }
  if (MHD_CONTENT_READER_END_WITH_ERROR == ret)
  {
    response->total_size = connection->rp.rsp_write_position;
#if defined(MHD_USE_THREADS)
    MHD_mutex_unlock_chk_ (&response->mutex);
#endif
    CONNECTION_CLOSE_ERROR (connection, "Closing connection (application error generating response)." );
    return MHD_NO;
  }
  if (MHD_CONTENT_READER_END_OF_STREAM == ret)
  {
    *p_finished = true;
    response->total_size = connection->rp.rsp_write_position;
    return MHD_YES;
  }
  if (0 == ret)
  {
    connection->state = MHD_CONNECTION_CHUNKED_BODY_UNREADY;
#if defined(MHD_USE_THREADS)
    MHD_mutex_unlock_chk_ (&response->mutex);
#endif
    return MHD_NO;
  }
  if (size_to_fill < (size_t) ret)
  {
#if defined(MHD_USE_THREADS)
    MHD_mutex_unlock_chk_ (&response->mutex);
#endif
    CONNECTION_CLOSE_ERROR (connection, "Closing connection (application returned more data than requested)." );
    return MHD_NO;
  }
  chunk_hdr_len = MHD_uint32_to_strx ((uint32_t) ret, chunk_hdr, sizeof(chunk_hdr));
  mhd_assert (chunk_hdr_len != 0);
  mhd_assert (chunk_hdr_len < sizeof(chunk_hdr));
  *p_finished = false;
  connection->write_buffer_send_offset = (max_chunk_hdr_len - (chunk_hdr_len + 2));
  memcpy (connection->write_buffer + connection->write_buffer_send_offset, chunk_hdr, chunk_hdr_len);
  connection->write_buffer[max_chunk_hdr_len - 2] = '\r';
  connection->write_buffer[max_chunk_hdr_len - 1] = '\n';
  connection->write_buffer[max_chunk_hdr_len + (size_t) ret] = '\r';
  connection->write_buffer[max_chunk_hdr_len + (size_t) ret + 1] = '\n';
  connection->rp.rsp_write_position += (size_t) ret;
  connection->write_buffer_append_offset = max_chunk_hdr_len + (size_t) ret + 2;
  return MHD_YES;
}
