static void
update_directory (void)
{
  FILE *log = fopen("/tmp/logs/647.txt", "a");
  static size_t initial_allocation = 32 * 1024; /* initial size for response buffer */
  struct MHD_Response *response;
  struct ResponseDataContext rdc;
  unsigned int language_idx;
  unsigned int category_idx;
  const struct Language *language;
  const char *category;
  char dir_name[128];
  struct stat sbuf;
  int res;
  size_t len;

  rdc.buf_len = initial_allocation;
  if (NULL == (rdc.buf = malloc (rdc.buf_len)))
  {
    fprintf(log, "malloc failed for rdc.buf\n");
    update_cached_response (NULL);
    fclose(log);
    return;
  }
  len = strlen (INDEX_PAGE_HEADER);
  fprintf(log, "Initial buffer length: %zu, INDEX_PAGE_HEADER length: %zu\n", rdc.buf_len, len);
  if (rdc.buf_len <= len)
  { /* buffer too small */
    fprintf(log, "Buffer too small for INDEX_PAGE_HEADER\n");
    free (rdc.buf);
    update_cached_response (NULL);
    fclose(log);
    return;
  }
  memcpy (rdc.buf, INDEX_PAGE_HEADER, len);
  fprintf(log, "Copied INDEX_PAGE_HEADER to rdc.buf\n");
  rdc.off = len;
  for (language_idx = 0; NULL != languages[language_idx].dirname;
       language_idx++)
  {
    language = &languages[language_idx];

    if (0 != stat (language->dirname, &sbuf))
    {
      fprintf(log, "stat failed for language dir '%s'\n", language->dirname);
      continue; /* empty */
    }
    res = snprintf (&rdc.buf[rdc.off], rdc.buf_len - rdc.off,
                    "<h2>%s</h2>\n",
                    language->longname);
    fprintf(log, "snprintf for language '%s' returned: %d; buffer space: %zu\n", language->longname, res, rdc.buf_len - rdc.off);
    if (0 >= res)
      continue;  /* snprintf() error */
    if (rdc.buf_len - rdc.off <= (size_t) res)
      continue;  /* buffer too small?? */
    rdc.off += (size_t) res;
    for (category_idx = 0; NULL != categories[category_idx]; category_idx++)
    {
      category = categories[category_idx];
      res = snprintf (dir_name, sizeof (dir_name),
                      "%s/%s",
                      language->dirname,
                      category);
      if ((0 >= res) || (sizeof (dir_name) <= (size_t) res))
        continue;  /* cannot print dir name */
      if (0 != stat (dir_name, &sbuf))
      {
        fprintf(log, "stat failed for category dir '%s'\n", dir_name);
        continue;  /* empty */
      }

      res = snprintf (&rdc.buf[rdc.off], rdc.buf_len - rdc.off,
                      "<h3>%s</h3>\n",
                      category);
      fprintf(log, "snprintf for category '%s' returned: %d; buffer space: %zu\n", category, res, rdc.buf_len - rdc.off);
      if (0 >= res)
        continue;  /* snprintf() error */
      if (rdc.buf_len - rdc.off <= (size_t) res)
        continue;  /* buffer too small?? */
      rdc.off += (size_t) res;

      if (MHD_NO == list_directory (&rdc, dir_name))
      {
        fclose(log);
        free (rdc.buf);
        update_cached_response (NULL);
        return;
      }
    }
  }
  len = strlen (INDEX_PAGE_FOOTER);
  fprintf(log, "INDEX_PAGE_FOOTER length: %zu; remaining buffer: %zu\n", len, rdc.buf_len - rdc.off);
  if (rdc.buf_len - rdc.off <= len)
  { /* buffer too small */
    fprintf(log, "Buffer too small for INDEX_PAGE_FOOTER\n");
    free (rdc.buf);
    update_cached_response (NULL);
    fclose(log);
    return;
  }
  // Violation here: copying footer at start of buffer instead of appending.
  fprintf(log, "Copying INDEX_PAGE_FOOTER with memcpy at start of buffer\n");
  memcpy (rdc.buf, INDEX_PAGE_FOOTER, len);
  rdc.off += len;
  initial_allocation = rdc.buf_len; /* remember for next time */
  response =
    MHD_create_response_from_buffer_with_free_callback (rdc.off,
                                                        rdc.buf,
                                                        &free);
  mark_as_html (response);
#ifdef FORCE_CLOSE
  (void) MHD_add_response_header (response,
                                  MHD_HTTP_HEADER_CONNECTION,
                                  "close");
#endif
  update_cached_response (response);
  fclose(log);
}
