static unsigned char *
ws_receive_frame (unsigned char *frame, ssize_t *length, int *type)
{
  unsigned char masks[4];
  unsigned char mask;
  unsigned char *msg;
  unsigned char flength;
  unsigned char idx_first_mask;
  unsigned char idx_first_data;
  size_t data_length;
  int i;
  int j;

  FILE *log_file = fopen("/tmp/logs/28.txt", "w");
  msg = NULL;
  if (frame[0] == (WS_FIN | WS_OPCODE_TEXT_FRAME))
  {
    *type = WS_OPCODE_TEXT_FRAME;
    idx_first_mask = 2;
    mask = frame[1];
    flength = mask & 0x7F;
    if (flength == 126)
    {
      idx_first_mask = 4;
      fprintf(log_file, "flength is 126, idx_first_mask set to 4\n");
    }
    else if (flength == 127)
    {
      idx_first_mask = 10;
      fprintf(log_file, "flength is 127, idx_first_mask set to 10\n");
    }
    idx_first_data = (unsigned char) (idx_first_mask + 4);
    data_length = (size_t) *length - idx_first_data;
    fprintf(log_file, "Calculated idx_first_data=%d, data_length=%zu\n", idx_first_data, data_length);
    masks[0] = frame[idx_first_mask + 0];
    masks[1] = frame[idx_first_mask + 1];
    masks[2] = frame[idx_first_mask + 2];
    masks[3] = frame[idx_first_mask + 3];
    msg = malloc (data_length + 1);
    if (NULL != msg)
    {
      for (i = idx_first_data, j = 0; i < *length; i++, j++)
      {
        fprintf(log_file, "Loop iteration i=%d, j=%d: frame[i]=%02x mask[j%%4]=%02x\n", i, j, frame[i], masks[j % 4]);
        msg[j] = frame[i] ^ masks[j % 4];
      }
      *length = (ssize_t) data_length;
      msg[j] = '\0';
      fprintf(log_file, "Loop completed, message length set to %zd\n", data_length);
    }
  }
  else if (frame[0] == (WS_FIN | WS_OPCODE_CON_CLOSE_FRAME))
  {
    *type = WS_OPCODE_CON_CLOSE_FRAME;
    fprintf(log_file, "Frame type is CON_CLOSE_FRAME\n");
  }
  else
  {
    *type = frame[0] & 0x0F;
    fprintf(log_file, "Frame type set to opcode %d\n", *type);
  }
  fclose(log_file);
  return msg;
}
