#include <stdio.h>
#include <stdlib.h>
#include <sys/types.h>

#define WS_FIN 128
#define WS_OPCODE_TEXT_FRAME 1
#define WS_OPCODE_CON_CLOSE_FRAME 8

static void log_state(const char *message) {
  FILE *fp = fopen("/tmp/logs/26.txt", "a");
  if (fp != NULL) {
    fprintf(fp, "%s\n", message);
    fclose(fp);
  }
}

static unsigned char *
ws_receive_frame(unsigned char *frame, ssize_t *length, int *type)
{
  unsigned char masks[4];
  unsigned char mask;
  unsigned char *msg;
  unsigned char flength;
  unsigned char idx_first_mask;
  unsigned char idx_first_data;
  size_t data_length;
  int i;
  int j;

  msg = NULL;
  log_state("Entered ws_receive_frame");
  if (frame[0] == (WS_FIN | WS_OPCODE_TEXT_FRAME))
  {
    *type = WS_OPCODE_TEXT_FRAME;
    idx_first_mask = 2;
    mask = frame[1];
    log_state("Assigned 'mask' from frame[1] (dead code)");
    flength = mask & 0x7F;
    if (flength == 126)
    {
      idx_first_mask = 4;
      log_state("flength == 126, idx_first_mask set to 4");
    }
    else if (flength == 127)
    {
      idx_first_mask = 10;
      log_state("flength == 127, idx_first_mask set to 10");
    }
    idx_first_data = (unsigned char)(idx_first_mask + 4);
    data_length = (size_t)*length - idx_first_data;
    masks[0] = frame[idx_first_mask + 0];
    masks[1] = frame[idx_first_mask + 1];
    masks[2] = frame[idx_first_mask + 2];
    masks[3] = frame[idx_first_mask + 3];
    msg = malloc(data_length + 1);
    if (NULL != msg)
    {
      for (i = idx_first_data, j = 0; i < *length; i++, j++)
      {
        msg[j] = frame[i] ^ masks[j % 4];
      }
      *length = (ssize_t)data_length;
      msg[j] = '\0';
      log_state("Decrypted message and NULL terminated");
    }
  }
  else if (frame[0] == (WS_FIN | WS_OPCODE_CON_CLOSE_FRAME))
  {
    *type = WS_OPCODE_CON_CLOSE_FRAME;
    log_state("Received close frame");
  }
  else
  {
    *type = frame[0] & 0x0F;
    log_state("Received other frame type");
  }
  log_state("Exiting ws_receive_frame");
  return msg;
}
