#include <stdio.h>
#include <stdint.h>
#include <stddef.h>
#include <string.h>

/* Minimal definitions to allow compilation while preserving original logic. */
enum { MD5_BLOCK_SIZE = 64 };

struct Md5Ctx
{
  size_t count;
  uint32_t H[4];
  uint32_t buffer[MD5_BLOCK_SIZE / 4];
};

/* External functions: declared but not defined here (no mocks). */
void mhd_assert (int cond);
void md5_transform (uint32_t H[4], const void *block);

void
MHD_MD5_update (struct Md5Ctx *ctx,
                const uint8_t *data,
                size_t length)
{
  unsigned int bytes_have; /* Number of bytes in the context buffer */
  FILE *log = fopen("/tmp/logs/661.txt", "a");
  if (log != NULL)
  {
    fprintf(log, "ENTER: ctx=%p data=%p length=%zu\n", (void *)ctx, (const void *)data, length);
    fflush(log);
  }

  mhd_assert ((data != NULL) || (length == 0));

#ifndef MHD_FAVOR_SMALL_CODE
  if (0 == length)
  {
    if (log != NULL)
    {
      fprintf(log, "SHORTCUT: length == 0 -> return\n");
      fclose(log);
    }
    return; /* Shortcut, do nothing */
  }
#endif /* MHD_FAVOR_SMALL_CODE */

  /* Note: (count & (MD5_BLOCK_SIZE-1))
           equals (count % MD5_BLOCK_SIZE) for this block size. */
  bytes_have = (unsigned int) (ctx->count & (MD5_BLOCK_SIZE - 1));
  ctx->count += length;

  if (log != NULL)
  {
    fprintf(log, "AFTER_COUNT_UPDATE: bytes_have=%u new_count=%zu\n", bytes_have, ctx->count);
    fflush(log);
  }

  if (0 != bytes_have)
  {
    unsigned int bytes_left = MD5_BLOCK_SIZE - bytes_have;
    if (log != NULL)
    {
      fprintf(log, "BRANCH: bytes_have!=0, bytes_left=%u, length=%zu\n", bytes_left, length);
      fflush(log);
    }
    if (length >= bytes_left)
    {     /* Combine new data with data in the buffer and
             process the full block. */
      if (log != NULL)
      {
        fprintf(log, "ACTION: copying %u bytes from data (%p) into ctx->buffer+%u\n",
                bytes_left, (const void *)data, bytes_have);
        fflush(log);
      }
      memcpy (((uint8_t *) ctx->buffer) + bytes_have,
              data,
              bytes_left);

      /* Log pointer and length state just before modification */
      if (log != NULL)
      {
        fprintf(log, "BEFORE_MODIFY: data=%p length=%zu\n", (const void *)data, length);
        fflush(log);
      }

      /* ---- Violation occurs here: modification of parameter 'data' ---- */
      data += bytes_left;
      length -= bytes_left;
      /* ---------------------------------------------------------------- */

      if (log != NULL)
      {
        fprintf(log, "AFTER_MODIFY: data=%p length=%zu\n", (const void *)data, length);
        fflush(log);
      }

      md5_transform (ctx->H, ctx->buffer);
      bytes_have = 0;

      if (log != NULL)
      {
        fprintf(log, "POST_TRANSFORM: bytes_have reset to %u\n", bytes_have);
        fflush(log);
      }
    }
    else
    {
      if (log != NULL)
      {
        fprintf(log, "NOT_ENOUGH_TO_FILL: length < bytes_left -> copy only and return\n");
        fflush(log);
      }
    }
  }

  while (MD5_BLOCK_SIZE <= length)
  {   /* Process any full blocks of new data directly,
         without copying to the buffer. */
    if (log != NULL)
    {
      fprintf(log, "WHILE_LOOP: processing full block at data=%p length=%zu\n", (const void *)data, length);
      fflush(log);
    }

    md5_transform (ctx->H, data);

    /* Log pointer and length state just before modification */
    if (log != NULL)
    {
      fprintf(log, "BEFORE_MODIFY_LOOP: data=%p length=%zu\n", (const void *)data, length);
      fflush(log);
    }

    /* ---- Violation occurs here as well: modification of parameter 'data' ---- */
    data += MD5_BLOCK_SIZE;
    length -= MD5_BLOCK_SIZE;
    /* -------------------------------------------------------------------- */

    if (log != NULL)
    {
      fprintf(log, "AFTER_MODIFY_LOOP: data=%p length=%zu\n", (const void *)data, length);
      fflush(log);
    }
  }

  if (0 != length)
  {   /* Copy incomplete block of new data (if any)
         to the buffer. */
    if (log != NULL)
    {
      fprintf(log, "TAIL_COPY: copying %zu bytes from data=%p into ctx->buffer+%u\n",
              length, (const void *)data, bytes_have);
      fprintf(log, "EXIT: final data=%p final_length=%zu\n", (const void *)data, length);
      fclose(log);
    }
    memcpy (((uint8_t *) ctx->buffer) + bytes_have, data, length);
  }
  else
  {
    if (log != NULL)
    {
      fprintf(log, "EXIT: no tail to copy, final data=%p final_length=%zu\n", (const void *)data, length);
      fclose(log);
    }
  }
}
