#include <stdio.h>
#include <string.h>

_MHD_EXTERN enum MHD_Result
MHD_digest_auth_calc_userdigest (enum MHD_DigestAuthAlgo3 algo3,
                                 const char *username,
                                 const char *realm,
                                 const char *password,
                                 void *userdigest_bin,
                                 size_t bin_buf_size)
{
  struct DigestAlgorithm da;
  enum MHD_Result ret;
  /* Open a log file to record control path and state relevant to the
     potential out-of-bounds access when calling strlen on caller-provided
     pointers. The logging does not change the original logic. */
  FILE *logf = fopen("/tmp/logs/567.txt", "a");
  if (logf != NULL)
  {
    fprintf(logf, "ENTER: MHD_digest_auth_calc_userdigest: algo3=%u, username_ptr=%p, realm_ptr=%p, password_ptr=%p, bin_buf_size=%zu\n",
            (unsigned) algo3, (const void *) username, (const void *) realm,
            (const void *) password, bin_buf_size);
  }

  if (! digest_init_one_time (&da, get_base_digest_algo (algo3)))
  {
    if (logf != NULL)
    {
      fprintf(logf, "EXIT: digest_init_one_time failed\n");
      fclose(logf);
    }
    return MHD_NO;
  }

  if (digest_get_size (&da) > bin_buf_size)
    ret = MHD_NO;
  else
  {
    /* Compute lengths exactly as original code did via strlen; we store
       them in locals so we can log them without changing the behavior. */
    size_t username_len = 0u;
    size_t realm_len = 0u;

    /* These strlen calls are the source of the potential Rule 21.17
       violation if the caller provided pointers that are not NUL-terminated
       within their object bounds. We log the computed lengths. */
    username_len = strlen (username);
    realm_len = strlen (realm);

    if (logf != NULL)
    {
      fprintf(logf, "BEFORE calc_userdigest: username_len=%zu, realm_len=%zu, expected_bin_size=%u\n",
              username_len, realm_len, (unsigned) digest_get_size(&da));
    }

    calc_userdigest (&da,
                     username,
                     username_len,
                     realm,
                     realm_len,
                     password,
                     userdigest_bin);
    ret = MHD_YES;

#ifdef MHD_DIGEST_HAS_EXT_ERROR
    if (digest_ext_error (&da))
      ret = MHD_NO;
#endif /* MHD_DIGEST_HAS_EXT_ERROR */

    if (logf != NULL)
    {
      fprintf(logf, "AFTER calc_userdigest: ret=%d\n", (int) ret);
    }
  }
  digest_deinit (&da);

  if (logf != NULL)
  {
    fprintf(logf, "EXIT: MHD_digest_auth_calc_userdigest returning %d\n", (int) ret);
    fclose(logf);
  }

  return ret;
}
