#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>
#include <stddef.h>

static void mhd_log_state(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/32.txt", "a");
    if (NULL == f)
        return;
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    fprintf(f, "\n");
    va_end(ap);
    fclose(f);
}

_MHD_EXTERN struct MHD_DigestAuthInfo *
MHD_digest_auth_get_request_info3 (struct MHD_Connection *connection)
{
  const struct MHD_RqDAuth *params;
  struct MHD_DigestAuthInfo *info;
  enum MHD_DigestAuthUsernameType uname_type;
  size_t unif_buf_size;
  uint8_t *unif_buf_ptr;
  size_t unif_buf_used;
  enum MHD_GetRqNCResult nc_res;

  params = MHD_get_rq_dauth_params_ (connection);
  if (NULL == params)
  {
    mhd_log_state("MHD_digest_auth_get_request_info3: no params (NULL), returning NULL");
    return NULL;
  }

  unif_buf_size = 0;

  uname_type = get_rq_uname_type (params);
  mhd_log_state("uname_type=%d", (int) uname_type);

  unif_buf_size += get_rq_unames_size (params, uname_type);
  mhd_log_state("after get_rq_unames_size: unif_buf_size=%zu", unif_buf_size);

  if (NULL != params->opaque.value.str)
  {
    unif_buf_size += params->opaque.value.len + 1;  /* Add one for zero-termination */
    mhd_log_state("opaque present: len=%zu, unif_buf_size=%zu", params->opaque.value.len, unif_buf_size);
  }
  if (NULL != params->realm.value.str)
  {
    unif_buf_size += params->realm.value.len + 1;   /* Add one for zero-termination */
    mhd_log_state("realm present: len=%zu, unif_buf_size=%zu", params->realm.value.len, unif_buf_size);
  }
  info = (struct MHD_DigestAuthInfo *)
         MHD_calloc_ (1, (sizeof(struct MHD_DigestAuthInfo)) + unif_buf_size);
  mhd_log_state("allocated info at %p (requested bytes=%zu)", (void *) info,
                (sizeof(struct MHD_DigestAuthInfo)) + unif_buf_size);
  unif_buf_ptr = (uint8_t *) (info + 1);
  unif_buf_used = 0;

  info->algo3 = params->algo3;
  mhd_log_state("algo3=%d", (int) info->algo3);

  if ( (MHD_DIGEST_AUTH_UNAME_TYPE_MISSING != uname_type) &&
       (MHD_DIGEST_AUTH_UNAME_TYPE_INVALID != uname_type) )
  {
    size_t used = get_rq_uname (params, uname_type,
                    (struct MHD_DigestAuthUsernameInfo *) info,
                    unif_buf_ptr + unif_buf_used,
                    unif_buf_size - unif_buf_used);
    unif_buf_used += used;
    mhd_log_state("get_rq_uname branch: used=%zu, unif_buf_used=%zu", used, unif_buf_used);
  }
  else
  {
    info->uname_type = uname_type;
    mhd_log_state("uname missing or invalid: uname_type=%d", (int) uname_type);
  }

  if (NULL != params->opaque.value.str)
  {
    info->opaque = (char *) (unif_buf_ptr + unif_buf_used);
    info->opaque_len = get_rq_param_unquoted_copy_z (&params->opaque,
                                                     info->opaque);
    unif_buf_used += info->opaque_len + 1;
    mhd_log_state("opaque copied: opaque_len=%zu, unif_buf_used=%zu", info->opaque_len, unif_buf_used);
  }
  if (NULL != params->realm.value.str)
  {
    info->realm = (char *) (unif_buf_ptr + unif_buf_used);
    info->realm_len = get_rq_param_unquoted_copy_z (&params->realm,
                                                    info->realm);
    unif_buf_used += info->realm_len + 1;
    mhd_log_state("realm copied: realm_len=%zu, unif_buf_used=%zu", info->realm_len, unif_buf_used);
  }

  mhd_assert (unif_buf_size >= unif_buf_used);
  mhd_log_state("buffer usage check: unif_buf_size=%zu, unif_buf_used=%zu", unif_buf_size, unif_buf_used);

  info->qop = params->qop;
  mhd_log_state("qop=%d", (int) info->qop);

  if (NULL != params->cnonce.value.str)
  {
    info->cnonce_len = params->cnonce.value.len;
    mhd_log_state("cnonce present: cnonce_len=%zu", info->cnonce_len);
  }
  else
  {
    info->cnonce_len = 0;
    mhd_log_state("cnonce missing: cnonce_len=0");
  }

  nc_res = get_rq_nc (params, &info->nc);
  mhd_log_state("get_rq_nc result=%d, nc=%u", (int) nc_res, (unsigned) info->nc);
  if (MHD_GET_RQ_NC_VALID != nc_res)
    info->nc = MHD_DIGEST_AUTH_INVALID_NC_VALUE;

  mhd_log_state("returning info at %p (nc=%u)", (void *) info, (unsigned) info->nc);
  return info;
}
