#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <stddef.h>

/* Probe helper that appends logs describing control path / state */
static void probe_log(const char *tag, size_t idx, size_t len)
{
  FILE *f = fopen("/tmp/logs/492.txt", "a");
  if (f != NULL)
  {
    fprintf(f, "%s: idx=%zu len=%zu\n", tag, idx, len);
    fclose(f);
  }
}

/* Original ahc_cb preserved as much as possible; small probes added only */
static enum MHD_Result
ahc_cb (void *cls, struct MHD_Connection *con, const char *url,
        const char *method, const char *version, const char *upload_data,
        size_t *upload_data_size, void **req_cls)
{
  struct MHD_Response *res;
  const char *upg_header;
  const char *con_header;
  const char *ws_version_header;
  const char *ws_key_header;
  char *ws_ac_value;
  enum MHD_Result ret;
  size_t key_size;

  (void) cls;               /* Unused. Silent compiler warning. */
  (void) url;               /* Unused. Silent compiler warning. */
  (void) upload_data;       /* Unused. Silent compiler warning. */
  (void) upload_data_size;  /* Unused. Silent compiler warning. */

  probe_log("ahc_cb:start", 0u, 0u);

  if (NULL == *req_cls)
  {
    *req_cls = (void *) 1;
    probe_log("ahc_cb:req_cls_set", 0u, 0u);
    return MHD_YES;
  }
  *req_cls = NULL;
  upg_header = MHD_lookup_connection_value (con, MHD_HEADER_KIND,
                                            MHD_HTTP_HEADER_UPGRADE);
  con_header = MHD_lookup_connection_value (con, MHD_HEADER_KIND,
                                            MHD_HTTP_HEADER_CONNECTION);
  if (MHD_NO == is_websocket_request (con, upg_header, con_header))
  {
    probe_log("ahc_cb:not_ws", 0u, 0u);
    return send_chat_page (con);
  }
  if ((0 != strcmp (method, MHD_HTTP_METHOD_GET))
      || (0 != strcmp (version, MHD_HTTP_VERSION_1_1)))
  {
    probe_log("ahc_cb:bad_method_or_version", 0u, 0u);
    return send_bad_request (con);
  }
  ws_version_header =
    MHD_lookup_connection_value (con, MHD_HEADER_KIND,
                                 MHD_HTTP_HEADER_SEC_WEBSOCKET_VERSION);
  if ((NULL == ws_version_header)
      || (0 != strcmp (ws_version_header, WS_SEC_WEBSOCKET_VERSION)))
  {
    probe_log("ahc_cb:bad_ws_version", 0u, 0u);
    return send_upgrade_required (con);
  }
  ret = MHD_lookup_connection_value_n (con, MHD_HEADER_KIND,
                                       MHD_HTTP_HEADER_SEC_WEBSOCKET_KEY,
                                       strlen (
                                         MHD_HTTP_HEADER_SEC_WEBSOCKET_KEY),
                                       &ws_key_header, &key_size);
  if ((MHD_NO == ret) || (key_size != WS_KEY_LEN))
  {
    probe_log("ahc_cb:no_key_or_bad_size", (size_t) key_size, WS_KEY_LEN);
    return send_bad_request (con);
  }

  /* probe before calling the accept-value generation that calls BASE64Encode */
  probe_log("ahc_cb:before_ws_get_accept_value", 0u, 0u);

  ret = ws_get_accept_value (ws_key_header, &ws_ac_value);
  if (MHD_NO == ret)
  {
    probe_log("ahc_cb:ws_get_accept_value_failed", 0u, 0u);
    return ret;
  }
  res = MHD_create_response_for_upgrade (&uh_cb, NULL);
  if (MHD_YES !=
      MHD_add_response_header (res, MHD_HTTP_HEADER_SEC_WEBSOCKET_ACCEPT,
                               ws_ac_value))
  {
    free (ws_ac_value);
    MHD_destroy_response (res);
    probe_log("ahc_cb:add_header_accept_failed", 0u, 0u);
    return MHD_NO;
  }
  free (ws_ac_value);
  if (MHD_YES !=
      MHD_add_response_header (res, MHD_HTTP_HEADER_UPGRADE, WS_UPGRADE_VALUE))
  {
    MHD_destroy_response (res);
    probe_log("ahc_cb:add_header_upgrade_failed", 0u, 0u);
    return MHD_NO;
  }
  ret = MHD_queue_response (con, MHD_HTTP_SWITCHING_PROTOCOLS, res);
  MHD_destroy_response (res);
  probe_log("ahc_cb:done", (size_t) ret, 0u);
  return ret;
}

/* Modified BASE64Encode: original logic preserved, probes log the loop counter
   progression and branch decisions so that the violation scenario (modifying
   the loop counter inside the body) can be traced. */
static ssize_t
BASE64Encode (const void *in, size_t len, char **output)
{
  const char *cvt = "ABCDEFGHIJKLMNOPQRSTUVWXYZ"
                    "abcdefghijklmnopqrstuvwxyz"
                    "0123456789+/";
  const char *data = (const char *) in;
  char *opt;
  ssize_t ret;
  size_t i;
  char c;

  ret = 0;

  opt = malloc (2 + (len * 4 / 3) + 8);
  if (NULL == opt)
  {
    probe_log("BASE64Encode:malloc_failed", 0u, len);
    return -1;
  }
  for (i = 0; i < len; ++i)
  {
    probe_log("BASE64Encode:loop_entry", i, len);
    c = (data[i] >> 2) & 0x3F;
    opt[ret++] = cvt[(int) c];
    c = (data[i] << 4) & 0x3F;
    if (++i < len)
    {
      /* Here the loop counter 'i' is incremented inside the loop body. */
      probe_log("BASE64Encode:branch_inc1", i, len);
      c = (char) (c | ((data[i] >> 4) & 0x0F));
    }
    opt[ret++] = cvt[(int) c];
    if (i < len)
    {
      c = (char) (c | ((data[i] << 2) & 0x3F));
      if (++i < len)
      {
        /* Another modification of loop counter inside the body. */
        probe_log("BASE64Encode:branch_inc2", i, len);
        c = (char) (c | ((data[i] >> 6) & 0x03));
      }
      opt[ret++] = cvt[(int) c];
    }
    else
    {
      ++i; /* and here */
      probe_log("BASE64Encode:branch_inc3_else", i, len);
      opt[ret++] = '='; /* FILLCHAR */
    }
    if (i < len)
    {
      c = data[i] & 0x3F;
      opt[ret++] = cvt[(int) c];
    }
    else
    {
      opt[ret++] = '='; /* FILLCHAR */
    }
    probe_log("BASE64Encode:loop_exit", i, len);
  }
  *output = opt;
  probe_log("BASE64Encode:finished", (size_t) ret, len);
  return ret;
}
