#include <stdio.h>
#include <string.h>
#include <stddef.h>
#include <stdlib.h>
#include <sys/stat.h>
#include <unistd.h>
#include <fcntl.h>

/* The function signature and many external symbols are left as in the
   original program so this translation unit can be compiled in the
   intended project context. We add safe guards and logging probes to
   document the violation scenario (empty URL) without introducing new
   out-of-bounds accesses. */

static void log_probe(const char *stage, const char *url, size_t url_len, const char *note)
{
  FILE *f = fopen("/tmp/logs/471.txt", "a");
  if (f != NULL)
  {
    fprintf(f, "%s: url=%s url_len=%zu note=%s\n",
            stage,
            (url != NULL) ? url : "(NULL)",
            url_len,
            (note != NULL) ? note : "");
    fclose(f);
  }
}

static enum MHD_Result
generate_page (void *cls,
               struct MHD_Connection *connection,
               const char *url,
               const char *method,
               const char *version,
               const char *upload_data,
               size_t *upload_data_size, void **req_cls)
{
  struct MHD_Response *response;
  enum MHD_Result ret;
  int fd;
  struct stat buf;
  (void) cls;               /* Unused. Silent compiler warning. */
  (void) version;           /* Unused. Silent compiler warning. */

  /* Compute url length safely for logging and bounds checks. */
  size_t url_len = 0u;
  if (url != NULL)
  {
    url_len = strlen(url);
  }
  log_probe("enter", url, url_len, "start generate_page");

  /* Preserve original check but avoid calling strcmp with a NULL pointer. */
  if ( (url == NULL) || (0 != strcmp (url, "/")) )
  {
    /* should be file download */
#ifdef MHD_HAVE_LIBMAGIC
    char file_data[MAGIC_HEADER_SIZE];
    ssize_t got;
#endif /* MHD_HAVE_LIBMAGIC */
    const char *mime;

    if ( (0 != strcmp (method, MHD_HTTP_METHOD_GET)) &&
         (0 != strcmp (method, MHD_HTTP_METHOD_HEAD)) )
      return MHD_NO;    /* unexpected method (we're not polite...) */
    fd = -1;

    /* Prepare a safe pointer to url[1] only if there is at least one
       character after url[0] (i.e. url_len >= 2). This avoids taking
       &url[1] when url is empty or only a single character. */
    const char *url1 = (url_len >= 2u) ? &url[1] : NULL;

    log_probe("file-branch", url, url_len,
              (url1 != NULL) ? "url1 available" : "url1 NOT available");

    if ( (url1 != NULL) &&
         (NULL == strstr (url1, "..")) &&
         ('/' != url[1]) )
    {
      /* safe to call open on &url[1] because url1 != NULL implies url_len >= 2 */
      fd = open (&url[1], O_RDONLY);
      log_probe("open_attempt", url, url_len, (fd != -1) ? "opened" : "open failed");
      if ( (-1 != fd) &&
           ( (0 != fstat (fd, &buf)) ||
             (! S_ISREG (buf.st_mode)) ) )
      {
        (void) close (fd);
        fd = -1;
      }
    }
    else
    {
      /* If url1 is NULL (e.g. empty URL) we avoid using &url[1] to prevent
         out-of-bounds accesses. The original code would have attempted the
         access; here we log that the code path would have triggered the
         unsafe access in the absence of guards. */
      log_probe("unsafe-avoid", url, url_len, "avoided &url[1] use to prevent OOB");
    }

    if (-1 == fd)
      return MHD_queue_response (connection,
                                 MHD_HTTP_NOT_FOUND,
                                 file_not_found_response);
#ifdef MHD_HAVE_LIBMAGIC
    /* read beginning of the file to determine mime type  */
    got = read (fd, file_data, sizeof (file_data));
    (void) lseek (fd, 0, SEEK_SET);
    if (0 < got)
      mime = magic_buffer (magic, file_data, (size_t) got);
    else
#endif /* MHD_HAVE_LIBMAGIC */
    mime = NULL;
    {
      /* Set mime-type by file-extension in some cases */
      const char *ldot = NULL;

      /* Only call strrchr if url1 is non-NULL (safe). */
      if (url1 != NULL)
      {
        ldot = strrchr (url1, '.');
        log_probe("strrchr", url, url_len, (ldot != NULL) ? "found dot" : "no dot");
      }

      if (NULL != ldot)
      {
        if (0 == strcasecmp (ldot,
                             ".html"))
          mime = "text/html";
        if (0 == strcasecmp (ldot,
                             ".css"))
          mime = "text/css";
        if (0 == strcasecmp (ldot,
                             ".css3"))
          mime = "text/css";
        if (0 == strcasecmp (ldot,
                             ".js"))
          mime = "application/javascript";
      }

    }

    if (NULL == (response = MHD_create_response_from_fd ((size_t) buf.st_size,
                                                         fd)))
    {
      /* internal error (i.e. out of memory) */
      (void) close (fd);
      return MHD_NO;
    }

    /* add mime type if we had one */
    if (NULL != mime)
      (void) MHD_add_response_header (response,
                                      MHD_HTTP_HEADER_CONTENT_TYPE,
                                      mime);
    ret = MHD_queue_response (connection,
                              MHD_HTTP_OK,
                              response);
    MHD_destroy_response (response);
    return ret;
  }

  if (0 == strcmp (method, MHD_HTTP_METHOD_POST))
  {
    /* upload! */
    struct UploadContext *uc = *req_cls;

    if (NULL == uc)
    {
      if (NULL == (uc = malloc (sizeof (struct UploadContext))))
        return MHD_NO; /* out of memory, close connection */
      memset (uc, 0, sizeof (struct UploadContext));
      uc->fd = -1;
      uc->connection = connection;
      uc->pp = MHD_create_post_processor (connection,
                                          64 * 1024 /* buffer size */,
                                          &process_upload_data, uc);
      if (NULL == uc->pp)
      {
        /* out of memory, close connection */
        free (uc);
        return MHD_NO;
      }
      *req_cls = uc;
      return MHD_YES;
    }
    if (0 != *upload_data_size)
    {
      if (NULL == uc->response)
        (void) MHD_post_process (uc->pp,
                                 upload_data,
                                 *upload_data_size);
      *upload_data_size = 0;
      return MHD_YES;
    }
    /* end of upload, finish it! */
    MHD_destroy_post_processor (uc->pp);
    uc->pp = NULL;
    if (-1 != uc->fd)
    {
      close (uc->fd);
      uc->fd = -1;
    }
    if (NULL != uc->response)
    {
      return MHD_queue_response (connection,
                                 MHD_HTTP_FORBIDDEN,
                                 uc->response);
    }
    else
    {
      update_directory ();
      return return_directory_response (connection);
    }
  }
  if ( (0 == strcmp (method, MHD_HTTP_METHOD_GET)) ||
       (0 == strcmp (method, MHD_HTTP_METHOD_HEAD)) )
  {
    return return_directory_response (connection);
  }

  /* unexpected request, refuse */
  return MHD_queue_response (connection,
                             MHD_HTTP_FORBIDDEN,
                             request_refused_response);
}
