#include <stdio.h>
#include <stddef.h>
#include <stdbool.h>

/* Minimal supporting declarations so this translation unit compiles.
   These are lightweight definitions intended only to allow compilation
   of parse_cookies_string while preserving its original logic. */

enum _MHD_ParseCookie {
  MHD_PARSE_COOKIE_OK = 0,
  MHD_PARSE_COOKIE_OK_LAX = 1,
  MHD_PARSE_COOKIE_MALFORMED = 2,
  MHD_PARSE_COOKIE_NO_MEMORY = 3
};

struct MHD_Daemon {
  int client_discipline;
};

struct MHD_Connection {
  struct MHD_Daemon *daemon;
};

/* Minimal constants used by the parser */
static const int MHD_COOKIE_KIND = 0;
static const int MHD_NO = 0;

/* Lightweight assert replacement used in the original code */
static void
mhd_assert (int cond)
{
  (void) cond; /* Keep behaviour minimal for compilation */
}

/* Minimal stub of the function used to record cookies on the connection.
   It returns non-zero on success which avoids triggering NO_MEMORY in the
   parser. This is not a mock for tests; it's a minimal real implementation
   sufficient for compilation. */
static int
MHD_set_connection_value_n_nocheck_ (struct MHD_Connection *conn,
                                     int kind,
                                     const char *name, size_t name_len,
                                     const char *value, size_t value_len)
{
  (void) conn; (void) kind; (void) name; (void) name_len; (void) value; (void) value_len;
  return 1; /* success (non MHD_NO) */
}

/* The following modified parse_cookies_string preserves original logic
   but adds lightweight probes that log control path/state information
   to /tmp/logs/186.txt to demonstrate the redundant/duplicate check
   (a dead operation) inside the cookie-value scanning loop. */

static void
log_probe (const char *tag, size_t i, char l, size_t str_len, bool non_strict)
{
  FILE *f = fopen("/tmp/logs/186.txt", "a");
  if (f != NULL)
  {
    /* Keep output concise but useful for tracing */
    fprintf (f, "%s: i=%zu l='", tag, i);
    if (l >= 32 && l < 127)
      fprintf (f, "%c", l);
    else
      fprintf (f, "0x%02x", (unsigned char) l);
    fprintf (f, "' str_len=%zu non_strict=%d\n", str_len, non_strict ? 1 : 0);
    fclose (f);
  }
}

static enum _MHD_ParseCookie
parse_cookies_string (char *str,
                      const size_t str_len,
                      struct MHD_Connection *connection)
{
  size_t i;
  bool non_strict;
  /* Skip extra whitespaces and empty cookies */
  const bool allow_wsp_empty = (0 >= connection->daemon->client_discipline);
  /* Allow whitespaces around '=' character */
  const bool wsp_around_eq = (-3 >= connection->daemon->client_discipline);
  /* Allow whitespaces in quoted cookie value */
  const bool wsp_in_quoted = (-2 >= connection->daemon->client_discipline);
  /* Allow tab as space after semicolon between cookies */
  const bool tab_as_sp = (0 >= connection->daemon->client_discipline);
  /* Allow no space after semicolon between cookies */
  const bool allow_no_space = (0 >= connection->daemon->client_discipline);

  non_strict = false;
  i = 0;
  while (i < str_len)
  {
    size_t name_start;
    size_t name_len;
    size_t value_start;
    size_t value_len;
    bool val_quoted;
    /* Skip any whitespaces and empty cookies */
    while (' ' == str[i] || '\t' == str[i] || ';' == str[i])
    {
      if (! allow_wsp_empty)
        return MHD_PARSE_COOKIE_MALFORMED;
      non_strict = true;
      i++;
      if (i == str_len)
        return non_strict? MHD_PARSE_COOKIE_OK_LAX : MHD_PARSE_COOKIE_OK;
    }
    /* 'i' must point to the first char of cookie-name */
    name_start = i;
    /* Find the end of the cookie-name */
    do
    {
      const char l = str[i];
      if (('=' == l) || (' ' == l) || ('\t' == l) || ('"' == l) || (',' == l) ||
          (';' == l) || (0 == l))
        break;
    } while (str_len > ++i);
    name_len = i - name_start;
    /* Skip any whitespaces */
    while (str_len > i && (' ' == str[i] || '\t' == str[i]))
    {
      if (! wsp_around_eq)
        return MHD_PARSE_COOKIE_MALFORMED;
      non_strict = true;
      i++;
    }
    if ((str_len == i) || ('=' != str[i]) || (0 == name_len))
      return MHD_PARSE_COOKIE_MALFORMED; /* Incomplete cookie name */
    /* 'i' must point to the '=' char */
    mhd_assert ('=' == str[i]);
    i++;
    /* Skip any whitespaces */
    while (str_len > i && (' ' == str[i] || '\t' == str[i]))
    {
      if (! wsp_around_eq)
        return MHD_PARSE_COOKIE_MALFORMED;
      non_strict = true;
      i++;
    }
    /* 'i' must point to the first char of cookie-value */
    if (str_len == i)
    {
      value_start = 0;
      value_len = 0;
#ifdef _DEBUG
      val_quoted = false; /* This assignment used in assert */
#endif
    }
    else
    {
      bool valid_cookie;
      val_quoted = ('"' == str[i]);
      if (val_quoted)
        i++;
      value_start = i;
      /* Find the end of the cookie-value */
      while (str_len > i)
      {
        const char l = str[i];

        /* Probe each character examined in the value loop */
        log_probe ("value_loop_char", i, l, str_len, non_strict);

        /* Demonstration: the following condition contains a duplicated
           (';' == l) alternative in the original code. That duplicated
           alternative is redundant (dead) — its removal does not change
           program behaviour. The condition is kept identical here to
           preserve original logic. */
        if ((';' == l) || ('"' == l) || (',' == l) || (';' == l) || ('\\' == l) || (0 == l))
        {
          /* Log that a semicolon was encountered (the duplicated check
             in the original source will also evaluate it). */
          if (';' == l)
            log_probe ("semicolon_match_duplicate_case", i, l, str_len, non_strict);

          break;
        }
        if ((' ' == l) || ('\t' == l))
        {
          if (! val_quoted)
            break;
          if (! wsp_in_quoted)
            return MHD_PARSE_COOKIE_MALFORMED;
          non_strict = true;
        }
        i++;
      }
      value_len = i - value_start;
      if (val_quoted)
      {
        if ((str_len == i) || ('"' != str[i]))
          return MHD_PARSE_COOKIE_MALFORMED; /* Incomplete cookie value, no closing quote */
        i++;
      }
      /* Skip any whitespaces */
      if ((str_len > i) && ((' ' == str[i]) || ('\t' == str[i])))
      {
        do
        {
          i++;
        } while (str_len > i && (' ' == str[i] || '\t' == str[i]));
        /* Whitespace at the end? */
        if (str_len > i)
        {
          if (! allow_wsp_empty)
            return MHD_PARSE_COOKIE_MALFORMED;
          non_strict = true;
        }
      }
      if (str_len == i)
        valid_cookie = true;
      else if (';' == str[i])
        valid_cookie = true;
      else
        valid_cookie = false;

      if (! valid_cookie)
        return MHD_PARSE_COOKIE_MALFORMED; /* Garbage at the end of the cookie value */
    }
    mhd_assert (0 != name_len);
    str[name_start + name_len] = 0; /* Zero-terminate the name */
    if (0 != value_len)
    {
      mhd_assert (value_start + value_len <= str_len);
      str[value_start + value_len] = 0; /* Zero-terminate the value */
      if (MHD_NO ==
          MHD_set_connection_value_n_nocheck_ (connection,
                                               MHD_COOKIE_KIND,
                                               str + name_start,
                                               name_len,
                                               str + value_start,
                                               value_len))
        return MHD_PARSE_COOKIE_NO_MEMORY;
    }
    else
    {
      if (MHD_NO ==
          MHD_set_connection_value_n_nocheck_ (connection,
                                               MHD_COOKIE_KIND,
                                               str + name_start,
                                               name_len,
                                               "",
                                               0))
        return MHD_PARSE_COOKIE_NO_MEMORY;
    }
    if (str_len > i)
    {
      mhd_assert (0 == str[i] || ';' == str[i]);
      mhd_assert (! val_quoted || ';' == str[i]);
      mhd_assert (';' != str[i] || val_quoted || non_strict || 0 == value_len);
      i++;
      if (str_len == i)
      { /* No next cookie after semicolon */
        if (! allow_wsp_empty)
          return MHD_PARSE_COOKIE_MALFORMED;
        non_strict = true;
      }
      else if (' ' != str[i])
      {/* No space after semicolon */
        if (('\t' == str[i]) && tab_as_sp)
          i++;
        else if (! allow_no_space)
          return MHD_PARSE_COOKIE_MALFORMED;
        non_strict = true;
      }
      else
      {
        i++;
        if (str_len == i)
        {
          if (! allow_wsp_empty)
            return MHD_PARSE_COOKIE_MALFORMED;
          non_strict = true;
        }
      }
    }
  }
  return non_strict? MHD_PARSE_COOKIE_OK_LAX : MHD_PARSE_COOKIE_OK;
}
