#include <stdio.h>
#include <stdlib.h>
#include <sys/types.h>

static unsigned char *
ws_receive_frame (unsigned char *frame, ssize_t *length, int *type)
{
  unsigned char masks[4];
  unsigned char mask;
  unsigned char *msg;
  unsigned char flength;
  unsigned char idx_first_mask;
  unsigned char idx_first_data;
  size_t data_length;
  int i;
  int j;

  /* Lightweight tracing to show control path and state for the close-frame scenario. */
  {
    FILE *log = fopen("/tmp/logs/26.txt", "a");
    if (log != NULL)
    {
      fprintf(log, "ENTER ws_receive_frame: frame0=0x%02X length=%zd\n", (unsigned int) frame[0], (ssize_t) *length);
      fclose(log);
    }
  }

  msg = NULL;
  if (frame[0] == (WS_FIN | WS_OPCODE_TEXT_FRAME))
  {
    *type = WS_OPCODE_TEXT_FRAME;
    idx_first_mask = 2;
    mask = frame[1];
    flength = mask & 0x7F;
    if (flength == 126)
    {
      idx_first_mask = 4;
    }
    else if (flength == 127)
    {
      idx_first_mask = 10;
    }
    idx_first_data = (unsigned char) (idx_first_mask + 4);
    data_length = (size_t) *length - idx_first_data;
    masks[0] = frame[idx_first_mask + 0];
    masks[1] = frame[idx_first_mask + 1];
    masks[2] = frame[idx_first_mask + 2];
    masks[3] = frame[idx_first_mask + 3];
    msg = malloc (data_length + 1);
    if (NULL != msg)
    {
      for (i = idx_first_data, j = 0; i < *length; i++, j++)
      {
        msg[j] = frame[i] ^ masks[j % 4];
      }
      *length = (ssize_t) data_length;
      msg[j] = '\0';
    }
  }
  else if (frame[0] == (WS_FIN | WS_OPCODE_CON_CLOSE_FRAME))
  {
    /* This assignment is the focus of the dead-code scenario: it is executed
       but in the current program the caller ignores *type when msg == NULL. */
    *type = WS_OPCODE_CON_CLOSE_FRAME;

    /* Log that we took the close-frame path and that msg remains NULL. */
    {
      FILE *log = fopen("/tmp/logs/26.txt", "a");
      if (log != NULL)
      {
        fprintf(log, "BRANCH close-frame: set *type=%d msg=%p (msg is NULL)\n", *type, (void *) msg);
        fclose(log);
      }
    }
  }
  else
  {
    *type = frame[0] & 0x0F;
  }

  /* Final state log for visibility. */
  {
    FILE *log = fopen("/tmp/logs/26.txt", "a");
    if (log != NULL)
    {
      fprintf(log, "EXIT ws_receive_frame: returned msg=%p type=%d length=%zd\n", (void *) msg, *type, (ssize_t) *length);
      fclose(log);
    }
  }

  return msg;
}
