#include <stdio.h>
#include <errno.h>
#include <pthread.h>
#include <stdlib.h>

// Dummy types and macros to allow compilation
typedef void *(*MHD_THREAD_START_ROUTINE_)(void *);

typedef struct {
    pthread_t handle;
    int ID;
} MHD_thread_handle_ID_;

void MHD_thread_handle_ID_set_invalid_(MHD_thread_handle_ID_ *h) {
    FILE *f = fopen("/tmp/logs/248.txt", "a");
    if (f) {
        fprintf(f, "Set invalid called for thread handle %p\n", (void *)h);
        fclose(f);
    }
}

void MHD_thread_handle_ID_set_native_handle_(MHD_thread_handle_ID_ *h, pthread_t native) {
    FILE *f = fopen("/tmp/logs/248.txt", "a");
    if (f) {
        fprintf(f, "Set native handle called: %lu\n", (unsigned long)native);
        fclose(f);
    }
    h->handle = native;
}


int
MHD_create_named_thread_ (MHD_thread_handle_ID_ *handle_id,
                          const char *thread_name,
                          size_t stack_size,
                          MHD_THREAD_START_ROUTINE_ start_routine,
                          void *arg)
{
#if defined(MHD_USE_THREAD_ATTR_SETNAME)
    int res;
    pthread_attr_t attr;
#if defined(MHD_thread_handle_ID_get_native_handle_ptr_)
    pthread_t *const new_tid_ptr =
        MHD_thread_handle_ID_get_native_handle_ptr_ (handle_id);
#else
    pthread_t new_tid;
    pthread_t *const new_tid_ptr = &new_tid;
#endif

    FILE *logf = fopen("/tmp/logs/248.txt", "a");
    if (logf) {
        fprintf(logf, "MHD_create_named_thread_: started with thread_name=%s\n", thread_name);
    }

    res = pthread_attr_init (&attr);
    if (0 == res)
    {
#if defined(HAVE_PTHREAD_ATTR_SETNAME_NP_NETBSD)
        if (logf) fprintf(logf, "Calling pthread_attr_setname_np (NetBSD variant)\n");
        res = pthread_attr_setname_np (&attr, thread_name, 0);
#elif defined(HAVE_PTHREAD_ATTR_SETNAME_NP_IBMI)
        if (logf) fprintf(logf, "Calling pthread_attr_setname_np (IBMI variant)\n");
        res = pthread_attr_setname_np (&attr, thread_name);
#else
#error No pthread_attr_setname_np() function.
#endif
        if ((res == 0) && (0 != stack_size) )
        {
            if (logf) fprintf(logf, "Setting stack size: %zu\n", stack_size);
            res = pthread_attr_setstacksize (&attr, stack_size);
        }
        if (0 == res)
        {
            if (logf) fprintf(logf, "Creating pthread\n");
            res = pthread_create (new_tid_ptr, &attr, start_routine, arg);
        }
        pthread_attr_destroy (&attr);
    }
    if (0 != res)
    {
        errno = res;
        if (logf) fprintf(logf, "Thread creation failed with res: %d\n", res);
        MHD_thread_handle_ID_set_invalid_ (handle_id);
    }
#if ! defined(MHD_thread_handle_ID_get_native_handle_ptr_)
    else
        MHD_thread_handle_ID_set_native_handle_ (handle_id, new_tid);
#endif
    if (logf) fclose(logf);

    return ! res;
#else
    return 0;
#endif
}
