static bool
get_request_line_inner (struct MHD_Connection *c)
{
  size_t p; /**< The current processing position */
  const int discp_lvl = c->daemon->client_discipline;
  const bool skip_empty_lines = (1 >= discp_lvl);
  const bool skip_several_empty_lines = (skip_empty_lines && (0 >= discp_lvl));
  const bool skip_unlimited_empty_lines = (skip_empty_lines && (-3 >= discp_lvl));
  const bool bare_lf_as_crlf = MHD_ALLOW_BARE_LF_AS_CRLF_ (discp_lvl);
  const bool tab_as_wsp = (0 >= discp_lvl);
  const bool other_wsp_as_wsp = (-1 >= discp_lvl);
  const bool wsp_blocks = (-1 >= discp_lvl);
  const bool wsp_in_uri = (0 >= discp_lvl);
  const bool wsp_in_uri_keep = (-2 >= discp_lvl);
  const bool bare_cr_keep = (wsp_in_uri_keep && (-3 >= discp_lvl));
  const bool bare_cr_as_sp = ((! bare_cr_keep) && (-1 >= discp_lvl));

  FILE *log_file = fopen("/tmp/logs/215.txt", "a");
  if (log_file != NULL)
  {
    fprintf(log_file, "Entered get_request_line_inner\n");
  }

  if (0 == c->read_buffer_offset)
  {
    if (log_file != NULL) fprintf(log_file, "No data to process: read_buffer_offset = 0\n");
    if (log_file != NULL) fclose(log_file);
    return false; /* No data to process */
  }
  p = c->rq.hdrs.rq_line.proc_pos;
  if (log_file != NULL) fprintf(log_file, "proc_pos start = %zu, read_buffer_offset = %zu\n", p, c->read_buffer_offset);

  if ((0 == p) && (skip_empty_lines))
  {
    bool is_empty_line;
    do
    {
      is_empty_line = false;
      if ('\r' == c->read_buffer[0])
      {
        if (1 == c->read_buffer_offset)
        {
          if (log_file != NULL) fprintf(log_file, "Not enough data after CR\n");
          if (log_file != NULL) fclose(log_file);
          return false; /* Not enough data yet */
        }
        if ('\n' == c->read_buffer[1])
        {
          is_empty_line = true;
          c->read_buffer += 2;
          c->read_buffer_size -= 2;
          c->read_buffer_offset -= 2;
          c->rq.hdrs.rq_line.skipped_empty_lines++;
          if (log_file != NULL) fprintf(log_file, "Skipped empty line (CRLF), total skipped: %u\n", c->rq.hdrs.rq_line.skipped_empty_lines);
        }
      }
      else if (('\n' == c->read_buffer[0]) && (bare_lf_as_crlf))
      {
        is_empty_line = true;
        c->read_buffer += 1;
        c->read_buffer_size -= 1;
        c->read_buffer_offset -= 1;
        c->rq.hdrs.rq_line.skipped_empty_lines++;
        if (log_file != NULL) fprintf(log_file, "Skipped empty line (LF as CRLF), total skipped: %u\n", c->rq.hdrs.rq_line.skipped_empty_lines);
      }
      if (is_empty_line)
      {
        if ((!skip_unlimited_empty_lines) && (((unsigned int)((skip_several_empty_lines) ? MHD_MAX_EMPTY_LINES_SKIP : 1)) < c->rq.hdrs.rq_line.skipped_empty_lines))
        {
          connection_close_error(c, _("Too many meaningless extra empty lines received before the request"));
          if (log_file != NULL) fprintf(log_file, "Too many empty lines; connection closed\n");
          if (log_file != NULL) fclose(log_file);
          return true;
        }
        if (0 == c->read_buffer_offset)
        {
          if (log_file != NULL) fprintf(log_file, "No more data after skipping empty lines\n");
          if (log_file != NULL) fclose(log_file);
          return false;  /* No more data to process */
        }
      }
    } while (is_empty_line);
  }

  c->state = MHD_CONNECTION_REQ_LINE_RECEIVING;

  while (p < c->read_buffer_offset)
  {
    const char chr = c->read_buffer[p];
    bool end_of_line = false;

    // Log current processing character
    if (log_file != NULL) fprintf(log_file, "Processing char '%c' at pos %zu\n", chr, p);

    if ('\r' == chr)
    {
      if (p + 1 == c->read_buffer_offset)
      {
        if (log_file != NULL) fprintf(log_file, "Not enough data after CR at pos %zu\n", p);
        c->rq.hdrs.rq_line.proc_pos = p;
        if (log_file != NULL) fclose(log_file);
        return false; /* Not enough data yet */
      }
      else if ('\n' == c->read_buffer[p + 1])
      {
        end_of_line = true;
      }
      else
      {
        if (bare_cr_as_sp)
        {
          c->read_buffer[p] = ' ';
          c->rq.num_cr_sp_replaced++;
          if (log_file != NULL) fprintf(log_file, "Replaced bare CR with space at pos %zu\n", p);
          continue;
        }
        else if (!bare_cr_keep)
        {
          if ((MHD_HTTP_MTHD_GET <= c->rq.http_mthd) && (MHD_HTTP_MTHD_DELETE >= c->rq.http_mthd))
          {
            transmit_error_response_static(c, MHD_HTTP_BAD_REQUEST, BARE_CR_IN_HEADER);
            if (log_file != NULL) fprintf(log_file, "Error: Bare CR in header at pos %zu\n", p);
          }
          else
          {
            connection_close_error(c, _("Bare CR characters are not allowed in the request line.\n"));
          }
          if (log_file != NULL) fclose(log_file);
          return true;
        }
      }
    }
    else if ('\n' == chr)
    {
      if (bare_lf_as_crlf)
        end_of_line = true;
      else
      {
        if ((MHD_HTTP_MTHD_GET <= c->rq.http_mthd) && (MHD_HTTP_MTHD_DELETE >= c->rq.http_mthd))
        {
          transmit_error_response_static(c, MHD_HTTP_BAD_REQUEST, BARE_LF_IN_HEADER);
          if (log_file != NULL) fprintf(log_file, "Error: Bare LF in header at pos %zu\n", p);
        }
        else
        {
          connection_close_error(c, _("Bare LF characters are not allowed in the request line.\n"));
        }
        if (log_file != NULL) fclose(log_file);
        return true;
      }
    }

    if (end_of_line)
    {
      if (NULL != c->rq.method)
      {
        if (wsp_in_uri)
        {
          if (0 != c->rq.hdrs.rq_line.last_ws_end)
          {
            if (NULL != c->rq.hdrs.rq_line.rq_tgt)
            {
              size_t idx = c->rq.hdrs.rq_line.last_ws_start;
              // Check boundary before zero terminate
              if (idx < c->read_buffer_offset)
              {
                c->read_buffer[idx] = 0; /* Zero terminate the URI */
                if (log_file != NULL) fprintf(log_file, "Zero-terminated URI at pos %zu\n", idx);
              }
              else
              {
                if (log_file != NULL) fprintf(log_file, "Index %zu out of bounds for zero termination\n", idx);
              }
              c->rq.req_target_len = idx - (size_t)(c->rq.hdrs.rq_line.rq_tgt - c->read_buffer);
            }
            else if ((c->rq.hdrs.rq_line.last_ws_start + 1 < c->rq.hdrs.rq_line.last_ws_end) && (HTTP_VER_LEN == (p - c->rq.hdrs.rq_line.last_ws_end)))
            {
              size_t idx = c->rq.hdrs.rq_line.last_ws_start + 1;
              if (idx < c->read_buffer_offset)
              {
                c->read_buffer[idx] = 0; /* Zero terminate the URI */
                if (log_file != NULL) fprintf(log_file, "Zero-terminated zero-length URI at pos %zu\n", idx);
              }
              else
              {
                if (log_file != NULL) fprintf(log_file, "Index %zu out of bounds for zero termination\n", idx);
              }
              c->rq.hdrs.rq_line.rq_tgt = c->read_buffer + idx;
              c->rq.req_target_len = 0;
              c->rq.hdrs.rq_line.num_ws_in_uri = 0;
              c->rq.hdrs.rq_line.rq_tgt_qmark = NULL;
            }
            if (NULL != c->rq.hdrs.rq_line.rq_tgt)
            {
              c->rq.version = c->read_buffer + c->rq.hdrs.rq_line.last_ws_end;
            }
          }
        }
        else
        {
          if ((NULL == c->rq.version) && (NULL != c->rq.hdrs.rq_line.rq_tgt) && (HTTP_VER_LEN == p - (size_t)(c->rq.hdrs.rq_line.rq_tgt - c->read_buffer)) && (0 != c->read_buffer[(size_t)(c->rq.hdrs.rq_line.rq_tgt - c->read_buffer) - 1]))
          {
            size_t uri_pos = (size_t)(c->rq.hdrs.rq_line.rq_tgt - c->read_buffer) - 1;
            if (uri_pos < p && uri_pos < c->read_buffer_offset)
            {
              c->rq.version = c->rq.hdrs.rq_line.rq_tgt;
              c->read_buffer[uri_pos] = 0;  /* Zero terminate the URI */
              if (log_file != NULL) fprintf(log_file, "Zero-terminated URI at pos %zu\n", uri_pos);
              c->rq.hdrs.rq_line.rq_tgt = c->read_buffer + uri_pos;
              c->rq.req_target_len = 0;
              c->rq.hdrs.rq_line.num_ws_in_uri = 0;
              c->rq.hdrs.rq_line.rq_tgt_qmark = NULL;
            }
            else
            {
              if (log_file != NULL) fprintf(log_file, "uri_pos %zu out of bounds, cannot zero terminate\n", uri_pos);
            }
          }
        }

        if (NULL != c->rq.version)
        {
          if (!parse_http_version(c, c->rq.version, p - (size_t)(c->rq.version - c->read_buffer)))
          {
            if (log_file != NULL) fprintf(log_file, "Unsupported HTTP version at pos %zu\n", p);
            if (log_file != NULL) fclose(log_file);
            return true; /* Unsupported / broken HTTP version */
          }
          if (p < c->read_buffer_offset)
          {
            c->read_buffer[p] = 0; /* Zero terminate the HTTP version strings */
            if (log_file != NULL) fprintf(log_file, "Zero terminated HTTP version string at pos %zu\n", p);
          }
          else
          {
            if (log_file != NULL) fprintf(log_file, "Index %zu out of bounds for zero termination of HTTP version\n", p);
          }

          if ('\r' == chr)
            p++;
          p++;
          c->read_buffer += p;
          c->read_buffer_size -= p;
          c->read_buffer_offset -= p;

          if (log_file != NULL) fprintf(log_file, "Request line parsed successfully\n");
          if (log_file != NULL) fclose(log_file);
          return true; /* Success */
        }
      }

      if ((MHD_HTTP_MTHD_GET <= c->rq.http_mthd) && (MHD_HTTP_MTHD_DELETE >= c->rq.http_mthd))
      {
        transmit_error_response_static(c, MHD_HTTP_BAD_REQUEST, REQUEST_MALFORMED);
        if (log_file != NULL) fprintf(log_file, "Request line malformed error sent\n");
      }
      else
      {
        connection_close_error(c, _("The request line is malformed.\n"));
        if (log_file != NULL) fprintf(log_file, "Request line malformed; connection closed\n");
      }

      if (log_file != NULL) fclose(log_file);
      return true;
    }

    p++;
  }

  c->rq.hdrs.rq_line.proc_pos = p;

  if (log_file != NULL)
  {
    fprintf(log_file, "Not enough data yet; updated proc_pos = %zu\n", p);
    fclose(log_file);
  }
  return false; /* Not enough data yet */
}
