static int
mhd_tool_get_sys_cpu_count_sysconf_fallback_ (void)
{
  FILE *log_file = fopen("/tmp/logs/290.txt", "w");
  int ret = -1;
#if defined(HAVE_SYSCONF) && \
  (defined(HAS_DECL__SC_CRAY_NCPU) || defined(HAS_DECL__SC_NPROCESSORS_CONF))
  long value = -1;
  if (log_file != NULL) fprintf(log_file, "Initial value: %ld\n", value);
#ifdef HAS_DECL__SC_CRAY_NCPU
  if (0 >= value) {
    value = sysconf (_SC_CRAY_NCPU);
    if (log_file != NULL) fprintf(log_file, "After _SC_CRAY_NCPU sysconf call, value: %ld\n", value);
  }
#endif /* HAS_DECL__SC_CRAY_NCPU */
#ifdef HAS_DECL__SC_NPROCESSORS_CONF
  if (0 >= value) {
    value = sysconf (_SC_NPROCESSORS_CONF);
    if (log_file != NULL) fprintf(log_file, "After _SC_NPROCESSORS_CONF sysconf call, value: %ld\n", value);
  }
#endif /* HAS_DECL__SC_NPROCESSORS_CONF */
  if (0 >= value) {
    if (log_file != NULL) fprintf(log_file, "Value <= 0, returning -1\n");
    if (log_file != NULL) fclose(log_file);
    return -1;
  }
  ret = (int) value;
  if (log_file != NULL) fprintf(log_file, "Value positive, casted ret: %d\n", ret);
  if ((long) ret != value) {
    if (log_file != NULL) fprintf(log_file, "Overflow detected, returning -1\n");
    if (log_file != NULL) fclose(log_file);
    return -1; /* Overflow */
  }
#endif /* HAVE_SYSCONF &&
          (HAS_DECL__SC_CRAY_NCPU || HAS_DECL__SC_NPROCESSORS_CONF) */
  if (log_file != NULL) fprintf(log_file, "Returning ret: %d\n", ret);
  if (log_file != NULL) fclose(log_file);
  return ret;
}
