#include <stdio.h>
#include <stdarg.h>
#include <string.h>
#include <errno.h>

static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/0.txt", "a");
    if (!f)
        return;
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fprintf(f, "\n");
    fclose(f);
}

static int metal_shmem_try_map(struct metal_page_size *ps, int fd, size_t size,
                   struct metal_io_region **result)
{
    size_t pages, page, phys_size;
    struct metal_io_region *io;
    metal_phys_addr_t *phys;
    uint8_t *virt;
    void *mem;
    int error;

    probe_log("enter metal_shmem_try_map: fd=%d size=%zu page_size=%zu mmap_flags=0x%x",
              fd, size, ps->page_size, ps->mmap_flags);

    size = metal_align_up(size, ps->page_size);
    pages = size / ps->page_size;

    probe_log("aligned size=%zu pages=%zu", size, pages);

    error = metal_map(fd, 0, size, 1, ps->mmap_flags, &mem);
    if (error) {
        probe_log("metal_map failed: error=%d (%s)", error, strerror(-error));
        metal_log(METAL_LOG_WARNING,
              "failed to mmap shmem %ld,0x%x - %s\n",
              size, ps->mmap_flags, strerror(-error));
        return error;
    }

    probe_log("mmap succeeded: mem=%p size=%zu", mem, size);

    error = mlock(mem, size);
    if (error) {
        probe_log("mlock failed: error=%d errno=%d (%s)", error, errno, strerror(errno));
        metal_log(METAL_LOG_WARNING, "failed to mlock shmem - %s\n",
              strerror(errno));
    }

    phys_size = sizeof(*phys) * pages;
    phys = malloc(phys_size);
    if (!phys) {
        probe_log("malloc(phys) failed for phys_size=%zu", phys_size);
        metal_unmap(mem, size);
        return -ENOMEM;
    }

    io = malloc(sizeof(*io));
    if (!io) {
        probe_log("malloc(io) failed");
        free(phys);
        metal_unmap(mem, size);
        return -ENOMEM;
    }

    probe_log("pagemap_fd=%d", _metal.pagemap_fd);

    if (_metal.pagemap_fd < 0) {
        phys[0] = 0;
        probe_log("no pagemap available, using offset as pa. phys[0]=%lu", (unsigned long)phys[0]);
        metal_log(METAL_LOG_WARNING,
        "shmem - failed to get va2pa mapping. use offset as pa.\n");
        metal_io_init(io, mem, phys, size, -1, 0, &metal_shmem_io_ops);
    } else {
        for (virt = mem, page = 0; page < pages; page++) {
            size_t offset = page * ps->page_size;

            probe_log("virt page %zu: virt=%p offset=%zu", page, (void *)(virt + offset), offset);

            error = metal_virt2phys(virt + offset, &phys[page]);
            if (error < 0) {
                phys[page] = METAL_BAD_OFFSET;
                probe_log("metal_virt2phys failed for page %zu error=%d, phys[%zu]=%lu",
                          page, error, page, (unsigned long)phys[page]);
            } else {
                probe_log("metal_virt2phys success for page %zu phys[%zu]=%lu",
                          page, page, (unsigned long)phys[page]);
            }
        }
        metal_io_init(io, mem, phys, size, ps->page_shift, 0,
            &metal_shmem_io_ops);
    }
    *result = io;

    probe_log("exit metal_shmem_try_map success io=%p", (void *)io);

    return 0;
}
