#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <errno.h>
#include <stdint.h>
#include <stddef.h>

/* Probe helper: append control path/state information to /tmp/logs/4.txt */
static void probe_log(const char *tag, void *virt, size_t page, size_t pages)
{
    FILE *f = fopen("/tmp/logs/4.txt", "a");
    if (!f)
        return;
    fprintf(f, "%s: virt=%p page=%zu pages=%zu\n", tag, virt, page, pages);
    fclose(f);
}

static int metal_shmem_try_map(struct metal_page_size *ps, int fd, size_t size,
                               struct metal_io_region **result)
{
    size_t pages, page, phys_size;
    struct metal_io_region *io;
    metal_phys_addr_t *phys;
    uint8_t *virt;
    void *mem;
    int error;

    size = metal_align_up(size, ps->page_size);
    pages = size / ps->page_size;

    error = metal_map(fd, 0, size, 1, ps->mmap_flags, &mem);
    if (error) {
        metal_log(METAL_LOG_WARNING,
                  "failed to mmap shmem %ld,0x%x - %s\n",
                  size, ps->mmap_flags, strerror(-error));
        return error;
    }

    error = mlock(mem, size);
    if (error) {
        metal_log(METAL_LOG_WARNING, "failed to mlock shmem - %s\n",
                  strerror(errno));
    }

    phys_size = sizeof(*phys) * pages;
    phys = malloc(phys_size);
    if (!phys) {
        metal_unmap(mem, size);
        return -ENOMEM;
    }

    io = malloc(sizeof(*io));
    if (!io) {
        free(phys);
        metal_unmap(mem, size);
        return -ENOMEM;
    }

    if (_metal.pagemap_fd < 0) {
        phys[0] = 0;
        metal_log(METAL_LOG_WARNING,
                  "shmem - failed to get va2pa mapping. use offset as pa.\n");
        metal_io_init(io, mem, phys, size, -1, 0, &metal_shmem_io_ops);
    } else {
        /*
         * The following for-statement has its first clause as:
         *   virt = mem, page = 0
         * This assigns both the loop counter (page) and another object (virt) in
         * the initial clause. This demonstrates the Rule 14.2 violation: the
         * first clause should only initialise the loop counter.
         */
        for (virt = mem, page = 0; page < pages; page++) {
            /* Log the state at the start of each iteration to capture the effect
             * of the initial clause (virt set in the first clause). */
            probe_log("for_iteration_start", virt, page, pages);

            size_t offset = page * ps->page_size;

            error = metal_virt2phys(virt + offset, &phys[page]);
            if (error < 0)
                phys[page] = METAL_BAD_OFFSET;
        }
        metal_io_init(io, mem, phys, size, ps->page_shift, 0,
                      &metal_shmem_io_ops);
    }
    *result = io;

    return 0;
}
