#include <stdio.h>
#include <stdarg.h>

/* Probe logger that appends control-path and state info to /tmp/logs/33.txt */
static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/33.txt", "a");
    if (f) {
        va_list ap;
        va_start(ap, fmt);
        vfprintf(f, fmt, ap);
        fputc('\n', f);
        va_end(ap);
        (void)fclose(f);
    }
}

/* Forward declaration of the internal opener; definition provided elsewhere in the project. */
extern int lfs_file_open_(lfs_t *lfs, lfs_file_t *file, const char *path, int flags);

int lfs_file_open(lfs_t *lfs, lfs_file_t *file, const char *path, int flags) {
    int err = LFS_LOCK(lfs->cfg);
    /* Log the lock result and input state */
    probe_log("lfs_file_open: LFS_LOCK returned %d, lfs=%p, file=%p, path=%s, flags=0x%X",
              err, (void*)lfs, (void*)file, (path != (const char*)0) ? path : "(null)", (unsigned)flags);

    if (err) {
        /* This branch is unreachable in the current project configuration where LFS_LOCK returns 0. */
        probe_log("lfs_file_open: early-return path taken with err=%d", err);
        return err;
    }

    LFS_TRACE("lfs_file_open(%p, %p, \"%s\", %x)",
            (void*)lfs, (void*)file, path, (unsigned)flags);
    LFS_ASSERT(!lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    err = lfs_file_open_(lfs, file, path, flags);

    probe_log("lfs_file_open: lfs_file_open_ returned %d", err);

    LFS_TRACE("lfs_file_open -> %d", err);
    LFS_UNLOCK(lfs->cfg);

    probe_log("lfs_file_open: unlocked and returning %d", err);
    return err;
}
