/* Probe logger that records control path and relevant state to /tmp/logs/51.txt */
static void lfs_log_probe(const char *stage, void *lfs, void *file,
        void *buffer, uint32_t size, int lock_err, int read_res) {
    FILE *f = fopen("/tmp/logs/51.txt", "a");
    if (f == NULL) {
        return;
    }
    fprintf(f, "stage=%s lfs=%p file=%p buffer=%p size=%" PRIu32 " lock_err=%d read_res=%d\n",
            stage, lfs, file, buffer, size, lock_err, read_res);
    fclose(f);
}

