#include <stdio.h>
#include <inttypes.h>

lfs_soff_t lfs_file_seek(lfs_t *lfs, lfs_file_t *file,
        lfs_soff_t off, int whence) {
    int err = LFS_LOCK(lfs->cfg);

    /* Probe: log control path and state for diagnosing unreachable branch */
    {
        FILE *f = fopen("/tmp/logs/57.txt", "a");
        if (f != NULL) {
            fprintf(f, "ENTER lfs_file_seek: lfs=%p file=%p off=%" PRId32 " whence=%d LFS_LOCK->%d\n",
                    (void*)lfs, (void*)file, off, whence, err);
            fclose(f);
        }
    }

    if (err) {
        /* Log the (unreachable in this project) early-return path */
        FILE *f = fopen("/tmp/logs/57.txt", "a");
        if (f != NULL) {
            fprintf(f, "lfs_file_seek: early return due to lock error=%d\n", err);
            fclose(f);
        }
        return err;
    }

    LFS_TRACE("lfs_file_seek(%p, %p, %"PRId32", %d)",
            (void*)lfs, (void*)file, off, whence);
    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    lfs_soff_t res = lfs_file_seek_(lfs, file, off, whence);

    /* Log result and exit path */
    {
        FILE *f = fopen("/tmp/logs/57.txt", "a");
        if (f != NULL) {
            fprintf(f, "EXIT lfs_file_seek: result=%" PRId32 "\n", res);
            fclose(f);
        }
    }

    LFS_UNLOCK(lfs->cfg);
    return res;
}
