#include <stdio.h>
#include <inttypes.h>

lfs_soff_t lfs_file_seek(lfs_t *lfs, lfs_file_t *file,
        lfs_soff_t off, int whence) {
    /* Probe: record entry and LFS_LOCK result */
    int err = LFS_LOCK(lfs->cfg);
    FILE *f = fopen("/tmp/logs/58.txt", "a");
    if (f) {
        fprintf(f, "ENTER lfs_file_seek: lfs=%p file=%p off=%" PRId32 " whence=%d LFS_LOCK=%d file->pos=%" PRId32 "\n",
                (void*)lfs, (void*)file, (int32_t)off, whence, err, (int32_t)file->pos);
        fclose(f);
    }

    /* Original check - under current macro definitions this branch is never taken
       (dead code) because LFS_LOCK(lfs->cfg) evaluates to 0. Keep the check to
       preserve original logic. */
    if (err) {
        FILE *f2 = fopen("/tmp/logs/58.txt", "a");
        if (f2) {
            fprintf(f2, "lfs_file_seek: early return due to lock err=%d\n", err);
            fclose(f2);
        }
        return err;
    }

    LFS_TRACE("lfs_file_seek(%p, %p, %" PRId32 ", %d)",
            (void*)lfs, (void*)file, off, whence);
    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    lfs_soff_t res = lfs_file_seek_(lfs, file, off, whence);

    /* Probe: record result and control path */
    FILE *f3 = fopen("/tmp/logs/58.txt", "a");
    if (f3) {
        fprintf(f3, "EXIT lfs_file_seek: result=%" PRId32 " file->pos=%" PRId32 "\n",
                (int32_t)res, (int32_t)file->pos);
        fclose(f3);
    }

    LFS_TRACE("lfs_file_seek -> %" PRId32, res);
    LFS_UNLOCK(lfs->cfg);
    return res;
}
