#include <stdio.h>
#include <inttypes.h>

lfs_ssize_t lfs_fs_size(lfs_t *lfs) {
    int err = LFS_LOCK(lfs->cfg);

    /* Probe logging: record the lock result and control path to a persistent file
       for diagnosing the unreachable-branch scenario. */
    FILE *log = fopen("/tmp/logs/177.txt", "a");
    if (log) {
        fprintf(log, "lfs_fs_size: entered, lfs=%p, LFS_LOCK returned=%d\n", (void*)lfs, err);
    }

    if (err) {
        /* This branch is unreachable when LFS_LOCK expands to a constant 0.
           We still preserve the original logic and log the (theoretical) path. */
        if (log) {
            fprintf(log, "lfs_fs_size: taking error branch, returning=%d\n", err);
            fclose(log);
        }
        return err;
    }

    if (log) {
        fprintf(log, "lfs_fs_size: lock succeeded, calling lfs_fs_size_()\n");
    }

    LFS_TRACE("lfs_fs_size(%p)", (void*)lfs);

    lfs_ssize_t res = lfs_fs_size_(lfs);

    if (log) {
        fprintf(log, "lfs_fs_size: lfs_fs_size_ returned=%" PRId32 "\n", res);
        fclose(log);
    }

    LFS_TRACE("lfs_fs_size -> %"PRId32, res);
    LFS_UNLOCK(lfs->cfg);
    return res;
}
