#include <stdio.h>
#include <stdarg.h>
#include <string.h>
#include <stddef.h>

/* Helper logging function that appends probe messages to /tmp/logs/10.txt */
static void OS_API_Init_Log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/10.txt", "a");
    if (f == NULL)
    {
        return;
    }

    va_list args;
    va_start(args, fmt);
    vfprintf(f, fmt, args);
    va_end(args);
    fprintf(f, "\n");
    fclose(f);
}

/* A probe function with a side-effect (logging) used as an argument to OS_DEBUG.
   If OS_DEBUG is defined as an empty replacement, calls like
   OS_DEBUG("... %s", probe_side_effect("..."));
   will not evaluate probe_side_effect(), which this test is intended to reveal. */
static const char *probe_side_effect(const char *tag)
{
    OS_API_Init_Log("PROBE_CALLED: %s", tag);
    return tag;
}

int32 OS_API_Init(void)
{
    int32          return_code = OS_SUCCESS;
    osal_objtype_t idtype;
    uint32         microSecPerSec;

    /* Log entry and current global state unconditionally */
    OS_API_Init_Log("ENTER: OS_API_Init() GlobalState=%ld", (long)OS_SharedGlobalVars.GlobalState);

    /* Probe: put a side-effectful argument into an OS_DEBUG call to detect if the argument gets evaluated */
    /* If OS_DEBUG expands to nothing, probe_side_effect will NOT run and there will be no PROBE_CALLED entry in the log. */
    OS_DEBUG("PROBE_ENTRY: %s", probe_side_effect("OS_API_Init_entry"));

    /*
     * If OSAL is already initialized, not really a big issue, just return.
     * This is not typically expected though, so its worth a debug statement.
     *
     * However this can validly occur when running tests on some platforms
     * without a reset/reload between invocations.
     */
    if (OS_SharedGlobalVars.GlobalState == OS_INIT_MAGIC_NUMBER)
    {
        OS_API_Init_Log("NOTE: ignored redundant OS_API_Init() call (GlobalState already initialized)");
        OS_DEBUG("NOTE: ignored redundant OS_API_Init() call\n");
        /* Probe the OS_DEBUG expansion at this return path */
        OS_DEBUG("PROBE_REDUndANT: %s", probe_side_effect("redundant_return"));
        return OS_SUCCESS;
    }

    /* Wipe global state structure to be sure everything is clean */
    memset(&OS_SharedGlobalVars, 0, sizeof(OS_SharedGlobalVars));

    /* Reset debug to default level if enabled */
#if defined(OSAL_CONFIG_DEBUG_PRINTF)
    OS_SharedGlobalVars.DebugLevel = 1;
#endif

    /* Set flag that says OSAL has been initialized */
    OS_SharedGlobalVars.GlobalState = OS_INIT_MAGIC_NUMBER;

    OS_API_Init_Log("After init wipe: GlobalState=%ld, DebugLevel=%d", (long)OS_SharedGlobalVars.GlobalState, (int)OS_SharedGlobalVars.DebugLevel);

    /* Initialize the common table that everything shares */
    return_code = OS_ObjectIdInit();
    OS_API_Init_Log("OS_ObjectIdInit() returned %d", (int)return_code);
    if (return_code != OS_SUCCESS)
    {
        OS_API_Init_Log("ERROR: OS_ObjectIdInit() failed: %d", (int)return_code);
        return return_code;
    }

    for (idtype = 0; idtype < OS_OBJECT_TYPE_USER; ++idtype)
    {
        /* Initialize the implementation first, as the shared layer depends on it */
        OS_API_Init_Log("Begin type init: idtype=0x%x", (unsigned int)idtype);
        return_code = OS_API_Impl_Init(idtype);
        OS_API_Init_Log("OS_API_Impl_Init(0x%x) returned %d", (unsigned int)idtype, (int)return_code);
        if (return_code != OS_SUCCESS)
        {
            OS_DEBUG("OS_API_Impl_Init(0x%x) failed to initialize: %d\n", (unsigned int)idtype, (int)return_code);
            /* Probe the OS_DEBUG argument evaluation on error path */
            OS_DEBUG("PROBE_IMPL_FAIL: %s", probe_side_effect("impl_init_fail"));
            OS_API_Init_Log("Impl init failed for idtype=0x%x, code=%d", (unsigned int)idtype, (int)return_code);
            break;
        }

        switch (idtype)
        {
            case OS_OBJECT_TYPE_OS_TASK:
                return_code = OS_TaskAPI_Init();
                break;
            case OS_OBJECT_TYPE_OS_QUEUE:
                return_code = OS_QueueAPI_Init();
                break;
            case OS_OBJECT_TYPE_OS_BINSEM:
                return_code = OS_BinSemAPI_Init();
                break;
            case OS_OBJECT_TYPE_OS_COUNTSEM:
                return_code = OS_CountSemAPI_Init();
                break;
            case OS_OBJECT_TYPE_OS_MUTEX:
                return_code = OS_MutexAPI_Init();
                break;
            case OS_OBJECT_TYPE_OS_MODULE:
                return_code = OS_ModuleAPI_Init();
                break;
            case OS_OBJECT_TYPE_OS_TIMEBASE:
                return_code = OS_TimeBaseAPI_Init();
                break;
            case OS_OBJECT_TYPE_OS_TIMECB:
                return_code = OS_TimerCbAPI_Init();
                break;
            case OS_OBJECT_TYPE_OS_STREAM:
                return_code = OS_FileAPI_Init();
                break;
            case OS_OBJECT_TYPE_OS_DIR:
                return_code = OS_DirAPI_Init();
                break;
            case OS_OBJECT_TYPE_OS_FILESYS:
                return_code = OS_FileSysAPI_Init();
                break;
            case OS_OBJECT_TYPE_OS_CONSOLE:
                return_code = OS_ConsoleAPI_Init();
                break;
            case OS_OBJECT_TYPE_OS_CONDVAR:
                return_code = OS_CondVarAPI_Init();
                break;
            default:
                break;
        }
        OS_API_Init_Log("Shared layer init for idtype=0x%x returned %d", (unsigned int)idtype, (int)return_code);
        if (return_code != OS_SUCCESS)
        {
            OS_DEBUG("ID type 0x%x shared layer failed to initialize: %d\n", (unsigned int)idtype, (int)return_code);
            /* Probe the OS_DEBUG argument evaluation on this failure path */
            OS_DEBUG("PROBE_SHARED_FAIL: %s", probe_side_effect("shared_layer_fail"));
            OS_API_Init_Log("Shared layer failed to initialize idtype=0x%x code=%d", (unsigned int)idtype, (int)return_code);
            break;
        }
    }

    if (return_code == OS_SUCCESS)
    {
        OS_API_Init_Log("Calling OS_NetworkAPI_Init()");
        return_code = OS_NetworkAPI_Init();
        OS_API_Init_Log("OS_NetworkAPI_Init() returned %d", (int)return_code);
    }

    if (return_code == OS_SUCCESS)
    {
        OS_API_Init_Log("Calling OS_SocketAPI_Init()");
        return_code = OS_SocketAPI_Init();
        OS_API_Init_Log("OS_SocketAPI_Init() returned %d", (int)return_code);
    }

    /*
     * Confirm that somewhere during initialization,
     * the time variables got set to something valid
     */
    if (return_code == OS_SUCCESS &&
        (OS_SharedGlobalVars.MicroSecPerTick == 0 || OS_SharedGlobalVars.TicksPerSecond == 0))
    {
        OS_DEBUG("Implementation failed to initialize tick time globals\n");
        /* Probe whether the OS_DEBUG argument is evaluated on this path */
        OS_DEBUG("PROBE_TICK_FAIL: %s", probe_side_effect("tick_time_fail"));
        OS_API_Init_Log("Tick time globals invalid MicroSecPerTick=%ld TicksPerSecond=%ld",
                        (long)OS_SharedGlobalVars.MicroSecPerTick,
                        (long)OS_SharedGlobalVars.TicksPerSecond);
        return_code = OS_ERROR;
    }

    microSecPerSec = OS_SharedGlobalVars.MicroSecPerTick * OS_SharedGlobalVars.TicksPerSecond;

    if (microSecPerSec != 1000000)
    {
        OS_DEBUG("Warning: Microsecs per sec value of %lu does not equal 1000000 (MicroSecPerTick: %ld   "
                 "TicksPerSecond: %ld)\n",
                 (unsigned long)microSecPerSec, (long)OS_SharedGlobalVars.MicroSecPerTick,
                 (long)OS_SharedGlobalVars.TicksPerSecond);
        /* Probe whether the OS_DEBUG warning argument is evaluated */
        OS_DEBUG("PROBE_MICROSEC_WARN: %s", probe_side_effect("microsec_warning"));
        OS_API_Init_Log("microSecPerSec=%lu MicroSecPerTick=%ld TicksPerSecond=%ld",
                        (unsigned long)microSecPerSec,
                        (long)OS_SharedGlobalVars.MicroSecPerTick,
                        (long)OS_SharedGlobalVars.TicksPerSecond);
    }

    if (return_code != OS_SUCCESS)
    {
        /*
         * Some part of init failed, so set global flag that says OSAL is in shutdown state.
         *
         * In particular if certain internal resources (such as the console utility task)
         * were created, this should cause those tasks to self-exit such that the system
         * is ultimately returned to the same state it started in.
         */
        OS_SharedGlobalVars.GlobalState = OS_SHUTDOWN_MAGIC_NUMBER;
        OS_API_Init_Log("Leaving with failure: return_code=%d GlobalState set to SHUTDOWN", (int)return_code);
    }
    else
    {
        OS_API_Init_Log("Leaving with success: return_code=%d", (int)return_code);
    }

    /* Final probe for exit path */
    OS_DEBUG("PROBE_EXIT: %s", probe_side_effect("OS_API_Init_exit"));

    return return_code;
}
