#include <stdio.h>
#include <stdarg.h>
#include <string.h>

/*
 * Probe logging helper functions.
 * These are inserted to record control flow and state information to /tmp/logs/10.txt
 * They are intentionally lightweight and do not modify the logic/state of OS_API_Init.
 */
static void os_api_probe_log(const char *label, const char *fmt, ...)
{
    FILE *fp = fopen("/tmp/logs/10.txt", "a");
    if (fp == NULL)
    {
        return;
    }

    fprintf(fp, "[%s] ", label);

    va_list args;
    va_start(args, fmt);
    vfprintf(fp, fmt, args);
    va_end(args);

    /* Ensure each entry ends with a newline for readability */
    fprintf(fp, "\n");
    fclose(fp);
}

static void os_api_probe_state(const char *label, int32 return_code, osal_objtype_t idtype)
{
    FILE *fp = fopen("/tmp/logs/10.txt", "a");
    if (fp == NULL)
    {
        return;
    }

    fprintf(fp, "[%s] return_code=%d idtype=0x%x GlobalState=0x%x MicroSecPerTick=%ld TicksPerSecond=%ld\n",
            label,
            (int)return_code,
            (unsigned int)idtype,
            (unsigned int)OS_SharedGlobalVars.GlobalState,
            (long)OS_SharedGlobalVars.MicroSecPerTick,
            (long)OS_SharedGlobalVars.TicksPerSecond);
    fclose(fp);
}

int32 OS_API_Init(void)
{
    int32          return_code = OS_SUCCESS;
    osal_objtype_t idtype;
    uint32         microSecPerSec;

    os_api_probe_log("ENTRY", "OS_API_Init() called");

    /*
     * If OSAL is already initialized, not really a big issue, just return.
     * This is not typically expected though, so its worth a debug statement.
     *
     * However this can validly occur when running tests on some platforms
     * without a reset/reload between invocations.
     */
    if (OS_SharedGlobalVars.GlobalState == OS_INIT_MAGIC_NUMBER)
    {
        /* Mirror the original OS_DEBUG message into the persistent probe log */
        os_api_probe_log("OS_DEBUG_REPLACEMENT", "NOTE: ignored redundant OS_API_Init() call");
        os_api_probe_state("REDUNDANT_INIT", return_code, 0);
        return OS_SUCCESS;
    }

    /* Wipe global state structure to be sure everything is clean */
    memset(&OS_SharedGlobalVars, 0, sizeof(OS_SharedGlobalVars));
    os_api_probe_log("STATE", "After memset of OS_SharedGlobalVars");

    /* Reset debug to default level if enabled */
#if defined(OSAL_CONFIG_DEBUG_PRINTF)
    OS_SharedGlobalVars.DebugLevel = 1;
    os_api_probe_log("STATE", "DebugLevel reset to 1 (OSAL_CONFIG_DEBUG_PRINTF enabled)");
#endif

    /* Set flag that says OSAL has been initialized */
    OS_SharedGlobalVars.GlobalState = OS_INIT_MAGIC_NUMBER;
    os_api_probe_log("STATE", "GlobalState set to OS_INIT_MAGIC_NUMBER (0x%x)", (unsigned int)OS_SharedGlobalVars.GlobalState);

    /* Initialize the common table that everything shares */
    return_code = OS_ObjectIdInit();
    os_api_probe_state("AFTER_ObjectIdInit", return_code, 0);
    if (return_code != OS_SUCCESS)
    {
        os_api_probe_log("ERROR", "OS_ObjectIdInit() failed: %d", (int)return_code);
        return return_code;
    }

    for (idtype = 0; idtype < OS_OBJECT_TYPE_USER; ++idtype)
    {
        os_api_probe_log("LOOP", "Initializing idtype 0x%x", (unsigned int)idtype);

        /* Initialize the implementation first, as the shared layer depends on it */
        return_code = OS_API_Impl_Init(idtype);
        os_api_probe_state("AFTER_ImplInit", return_code, idtype);
        if (return_code != OS_SUCCESS)
        {
            /* Mirror the original debug message to the probe log */
            os_api_probe_log("OS_DEBUG_REPLACEMENT", "OS_API_Impl_Init(0x%x) failed to initialize: %d", (unsigned int)idtype, (int)return_code);
            break;
        }

        switch (idtype)
        {
            case OS_OBJECT_TYPE_OS_TASK:
                return_code = OS_TaskAPI_Init();
                break;
            case OS_OBJECT_TYPE_OS_QUEUE:
                return_code = OS_QueueAPI_Init();
                break;
            case OS_OBJECT_TYPE_OS_BINSEM:
                return_code = OS_BinSemAPI_Init();
                break;
            case OS_OBJECT_TYPE_OS_COUNTSEM:
                return_code = OS_CountSemAPI_Init();
                break;
            case OS_OBJECT_TYPE_OS_MUTEX:
                return_code = OS_MutexAPI_Init();
                break;
            case OS_OBJECT_TYPE_OS_MODULE:
                return_code = OS_ModuleAPI_Init();
                break;
            case OS_OBJECT_TYPE_OS_TIMEBASE:
                return_code = OS_TimeBaseAPI_Init();
                break;
            case OS_OBJECT_TYPE_OS_TIMECB:
                return_code = OS_TimerCbAPI_Init();
                break;
            case OS_OBJECT_TYPE_OS_STREAM:
                return_code = OS_FileAPI_Init();
                break;
            case OS_OBJECT_TYPE_OS_DIR:
                return_code = OS_DirAPI_Init();
                break;
            case OS_OBJECT_TYPE_OS_FILESYS:
                return_code = OS_FileSysAPI_Init();
                break;
            case OS_OBJECT_TYPE_OS_CONSOLE:
                return_code = OS_ConsoleAPI_Init();
                break;
            case OS_OBJECT_TYPE_OS_CONDVAR:
                return_code = OS_CondVarAPI_Init();
                break;
            default:
                break;
        }

        os_api_probe_state("AFTER_SHARED_INIT", return_code, idtype);

        if (return_code != OS_SUCCESS)
        {
            /* Mirror the original debug message to the probe log */
            os_api_probe_log("OS_DEBUG_REPLACEMENT", "ID type 0x%x shared layer failed to initialize: %d", (unsigned int)idtype, (int)return_code);
            break;
        }
    }

    os_api_probe_state("BEFORE_NETWORK", return_code, idtype);
    if (return_code == OS_SUCCESS)
    {
        return_code = OS_NetworkAPI_Init();
        os_api_probe_log("ACTION", "OS_NetworkAPI_Init() returned %d", (int)return_code);
    }

    if (return_code == OS_SUCCESS)
    {
        return_code = OS_SocketAPI_Init();
        os_api_probe_log("ACTION", "OS_SocketAPI_Init() returned %d", (int)return_code);
    }

    /*
     * Confirm that somewhere during initialization,
     * the time variables got set to something valid
     */
    if (return_code == OS_SUCCESS &&
        (OS_SharedGlobalVars.MicroSecPerTick == 0 || OS_SharedGlobalVars.TicksPerSecond == 0))
    {
        /* Mirror original debug message so probes capture the situation */
        os_api_probe_log("OS_DEBUG_REPLACEMENT", "Implementation failed to initialize tick time globals");
        os_api_probe_state("TICK_ERROR", return_code, idtype);
        return_code = OS_ERROR;
    }

    microSecPerSec = OS_SharedGlobalVars.MicroSecPerTick * OS_SharedGlobalVars.TicksPerSecond;
    os_api_probe_log("STATE", "Computed microSecPerSec=%lu (MicroSecPerTick=%ld TicksPerSecond=%ld)",
                     (unsigned long)microSecPerSec, (long)OS_SharedGlobalVars.MicroSecPerTick,
                     (long)OS_SharedGlobalVars.TicksPerSecond);

    if (microSecPerSec != 1000000)
    {
        /* Mirror the original warning message into the probe log */
        os_api_probe_log("OS_DEBUG_REPLACEMENT", "Warning: Microsecs per sec value of %lu does not equal 1000000 (MicroSecPerTick: %ld   TicksPerSecond: %ld)",
                         (unsigned long)microSecPerSec, (long)OS_SharedGlobalVars.MicroSecPerTick,
                         (long)OS_SharedGlobalVars.TicksPerSecond);
    }

    if (return_code != OS_SUCCESS)
    {
        /*
         * Some part of init failed, so set global flag that says OSAL is in shutdown state.
         *
         * In particular if certain internal resources (such as the console utility task)
         * were created, this should cause those tasks to self-exit such that the system
         * is ultimately returned to the same state it started in.
         */
        OS_SharedGlobalVars.GlobalState = OS_SHUTDOWN_MAGIC_NUMBER;
        os_api_probe_log("STATE", "Initialization failed. GlobalState set to OS_SHUTDOWN_MAGIC_NUMBER (0x%x)", (unsigned int)OS_SharedGlobalVars.GlobalState);
    }

    os_api_probe_state("EXIT", return_code, idtype);
    return return_code;
}
