suspicious_frags:
if (return_code == OS_SUCCESS)
        {
            /* the entire message was successfully written */
            console->WritePos = PendingWritePos;
        }
        else
        {
            /* the message did not fit */
            ++console->OverflowEvents;
        }

if (console->IsAsync)
        {
            /* post the sem for the utility task to run */
            OS_ConsoleWakeup_Impl(&token);
        }
        else
        {
            /* output directly */
            OS_ConsoleOutput_Impl(&token);
        }

if (WriteOffset == console->ReadPos)
        {
            /* out of space */
            return_code = OS_QUEUE_FULL;
            break;
        }

while (DataLen > 0)
    {
        /* writes the raw data directly to STDOUT_FILENO (unbuffered) */
        WriteLen = write(STDOUT_FILENO, Str, DataLen);
        if (WriteLen <= 0)
        {
            /* no recourse if this fails, just stop. */
            break;
        }
        Str += WriteLen;
        DataLen -= WriteLen;
    }

while (StartPos != EndPos)
    {
        if (StartPos > EndPos)
        {
            /* handle wrap */
            WriteSize = console->BufSize - StartPos;
        }
        else
        {
            WriteSize = EndPos - StartPos;
        }

        OS_BSP_ConsoleOutput_Impl(&console->BufBase[StartPos], WriteSize);

        StartPos += WriteSize;
        if (StartPos >= console->BufSize)
        {
            /* handle wrap */
            StartPos = 0;
        }
    }
