void OS_DeleteAllObjects(void)
{
    uint32 ObjectCount;
    uint32 TryCount;
    FILE *log = fopen("/tmp/logs/66.txt", "a");

    if (log != NULL)
    {
        fprintf(log, "OS_DeleteAllObjects: entry\n");
        fprintf(log, "Initial state: logging enabled\n");
        fflush(log);
    }

    /*
     * Note - this is done in a loop because some objects depend on other objects
     * and you will not be able to delete the object until the ref count becomes zero.
     */
    TryCount = 0;
    while (true)
    {
        ObjectCount = 0;
        ++TryCount;

        if (log != NULL)
        {
            fprintf(log, "Iteration start: TryCount=%u, ObjectCount reset to %u\n",
                    (unsigned)TryCount, (unsigned)ObjectCount);
            fflush(log);
        }

        /* Delete timers and tasks first, as they could be actively using other object types  */
        if (log != NULL) { fprintf(log, "Calling OS_ForEachObjectOfType(OS_OBJECT_TYPE_OS_TIMECB)\n"); fflush(log); }
        OS_ForEachObjectOfType(OS_OBJECT_TYPE_OS_TIMECB, OS_OBJECT_CREATOR_ANY, OS_CleanUpObject, &ObjectCount);
        if (log != NULL) { fprintf(log, "Returned from TIMECB loop, ObjectCount=%u\n", (unsigned)ObjectCount); fflush(log); }

        if (log != NULL) { fprintf(log, "Calling OS_ForEachObjectOfType(OS_OBJECT_TYPE_OS_TIMEBASE)\n"); fflush(log); }
        OS_ForEachObjectOfType(OS_OBJECT_TYPE_OS_TIMEBASE, OS_OBJECT_CREATOR_ANY, OS_CleanUpObject, &ObjectCount);
        if (log != NULL) { fprintf(log, "Returned from TIMEBASE loop, ObjectCount=%u\n", (unsigned)ObjectCount); fflush(log); }

        if (log != NULL) { fprintf(log, "Calling OS_ForEachObjectOfType(OS_OBJECT_TYPE_OS_TASK)\n"); fflush(log); }
        OS_ForEachObjectOfType(OS_OBJECT_TYPE_OS_TASK, OS_OBJECT_CREATOR_ANY, OS_CleanUpObject, &ObjectCount);
        if (log != NULL) { fprintf(log, "Returned from TASK loop, ObjectCount=%u\n", (unsigned)ObjectCount); fflush(log); }

        /* Then try to delete all other remaining objects of any type */
        if (log != NULL) { fprintf(log, "Calling OS_ForEachObject (all remaining types)\n"); fflush(log); }
        OS_ForEachObject(OS_OBJECT_CREATOR_ANY, OS_CleanUpObject, &ObjectCount);
        if (log != NULL) { fprintf(log, "Returned from OS_ForEachObject, ObjectCount=%u\n", (unsigned)ObjectCount); fflush(log); }

        if (ObjectCount == 0 || TryCount > 4)
        {
            if (log != NULL)
            {
                if (ObjectCount == 0)
                {
                    fprintf(log, "Breaking out: ObjectCount == 0 (all objects deleted)\n");
                }
                if (TryCount > 4)
                {
                    fprintf(log, "Breaking out: TryCount > 4 (TryCount=%u)\n", (unsigned)TryCount);
                }
                fflush(log);
                fclose(log);
                log = NULL;
            }
            break;
        }

        if (log != NULL)
        {
            fprintf(log, "Objects remain (ObjectCount=%u). Calling OS_TaskDelay(5) and retrying.\n", (unsigned)ObjectCount);
            fflush(log);
        }
        OS_TaskDelay(5);
    }
}
